    { name: "Pesquisa de Pre√ßos", departmentId: 3, phase: "Prepara√ß√£o", daysLimit: 10 },
    { name: "Mapa Comparativo de Pre√ßos", departmentId: 3, phase: "Prepara√ß√£o", daysLimit: 10 },
    { name: "Metodologia da Pesquisa de Pre√ßos", departmentId: 3, phase: "Prepara√ß√£o", daysLimit: 10 },

