    { name: "Autoriza√ß√£o pelo Secret√°rio SEAP", departmentId: 5, phase: "Autoriza√ß√£o", daysLimit: 2 },

    // CONTINUA√á√ÉO DO FLUXO - ETAPAS CONDICIONAIS B√ÅSICAS
    // Estas etapas ser√£o criadas dinamicamente baseadas nas decis√µes anteriores
    { name: "Inserir Dota√ß√£o Or√ßament√°ria", departmentId: 2, phase: "Execu√ß√£o", daysLimit: 10 },
    { name: "Elaborar Edital e seus Anexos", departmentId: 2, phase: "Execu√ß√£o", daysLimit: 10 },
    { name: "Consultar Comit√™ Gestor de Gasto P√∫blico - CGGP", departmentId: 2, phase: "Execu√ß√£o", daysLimit: 10 },

    // COMIT√ä GESTOR DE PLANO DE CONTING√äNCIA - CGPC (ID verificado: 6)
    { name: "Autorizar - Comit√™ Gestor de Plano de Conting√™ncia", departmentId: 6, phase: "Execu√ß√£o", daysLimit: 3 },

    // UNIDADE T√âCNICO NORMATIVA (ID verificado: 7)
    { name: "Elaborar Nota T√©cnica", departmentId: 7, phase: "Execu√ß√£o", daysLimit: 3 },

    // SECRET√ÅRIO SEAP - SOLICITAR PARECER PGE (usando ID do Secret√°rio: 5)
    { name: "Solicitar Parecer da PGE", departmentId: 5, phase: "Execu√ß√£o", daysLimit: 2 },

    // PROCURADORIA GERAL DO ESTADO - PGE (usando ID 8 temporariamente - verificar se existe)
    { name: "Parecer Jur√≠dico - PGE", departmentId: 8, phase: "Execu√ß√£o", daysLimit: 10 },

    // FINALIZA√á√ÉO - DIVIS√ÉO DE LICITA√á√ÉO
    { name: "Publicar Edital", departmentId: 2, phase: "Publica√ß√£o", daysLimit: 5 },
