export async function registerRoutes(app: Express): Promise<Server> {
  // Configura√ß√£o para servir arquivos est√°ticos da pasta de downloads
  app.use('/downloads', express.static(path.join(process.cwd(), 'public', 'downloads'), {
    setHeaders: (res, path) => {
      if (path.endsWith('.rar') || path.endsWith('.zip')) {
        res.setHeader('Content-Type', 'application/octet-stream');
        res.setHeader('Content-Disposition', 'attachment');
      }
    }
  }));

  // Rota direta para download do arquivo (agora usando o middleware static)
  app.get('/download-app', (req, res) => {
    // Redirecionar para o arquivo est√°tico
    res.redirect('/downloads/SEAP-PB-v1.0.0.zip');
  });

  // Rota para a p√°gina de download p√∫blica - sem restri√ß√£o de autentica√ß√£o
  app.get('/download', (req, res) => {
    // Log de acesso para fins de auditoria
    console.log('Acesso √† p√°gina de download p√∫blica');
    res.sendFile(path.join(process.cwd(), 'public', 'download.html'));
  });
  // Configurar trust proxy para que as sess√µes funcionem corretamente em produ√ß√£o
  app.set('trust proxy', 1);

  // Setup session
  const SessionStore = MemoryStore(session);
  app.use(session({
    name: 'seap-session',
    secret: process.env.SESSION_SECRET || 'seap-pb-bidding-system-secure-key-2025',
    resave: true,
    saveUninitialized: true,
    cookie: {
      maxAge: 30 * 24 * 60 * 60 * 1000, // 30 dias
      secure: false, // Sempre false para garantir compatibilidade
      httpOnly: true,
      sameSite: 'lax',
      path: '/'
    },
    store: new SessionStore({
      checkPeriod: 86400000 // 24 hours
    }),
  }));

  // Setup passport
  app.use(passport.initialize());
  app.use(passport.session());

  passport.use(new LocalStrategy(async (username, password, done) => {
    try {
      const user = await storage.authenticateUser(username, password);

      if (!user) {
        return done(null, false, { message: "Credenciais inv√°lidas." });
      }

      if (!user.isActive) {
        return done(null, false, { message: "Sua conta ainda n√£o foi ativada por um administrador." });
      }

      return done(null, user);
    } catch (error) {
      console.error("Erro na autentica√ß√£o:", error);
      return done(error);
    }
  }));

  passport.serializeUser((user: any, done) => {
    done(null, user.id);
  });

  passport.deserializeUser(async (id: number, done) => {
    try {
      const user = await storage.getUser(id);
      done(null, user || false);
    } catch (error) {
      done(error);
    }
  });

  // Authentication endpoints
  app.post('/api/auth/login', (req, res, next) => {
    passport.authenticate('local', (err, user, info) => {
      if (err) {
        console.error("Erro na autentica√ß√£o:", err);
        return res.status(500).json({ message: "Erro interno na autentica√ß√£o" });
      }

      if (!user) {
        return res.status(401).json({ message: info?.message || "Credenciais inv√°lidas" });
      }

      req.login(user, (loginErr) => {
        if (loginErr) {
          console.error("Erro no login:", loginErr);
          return res.status(500).json({ message: "Erro ao estabelecer sess√£o" });
        }

        console.log("Login bem-sucedido para:", user.username);
        return res.json(user);
      });
    })(req, res, next);
  });

  app.get('/api/auth/status', (req, res) => {
    if (req.isAuthenticated()) {
      res.json(req.user);
    } else {
      res.status(401).json({ message: "N√£o autenticado" });
    }
  });

  app.post('/api/auth/logout', (req, res) => {
    req.logout(() => {
      res.json({ success: true });
    });
  });

  // Registro de usu√°rios p√∫blicos
  app.post('/api/auth/register', async (req, res) => {
    try {
      // Valide os dados de entrada
      const validatedData = insertUserSchema.parse({
        ...req.body,
        role: 'common' // For√ßa o papel como 'common' para seguran√ßa
      });

      // Verifique se o nome de usu√°rio j√° existe
      const existingUser = await storage.getUserByUsername(validatedData.username);
      if (existingUser) {
        return res.status(400).json({ message: "Nome de usu√°rio j√° existe" });
      }

      // Hash da senha antes de armazenar
      const hashedPassword = await bcrypt.hash(validatedData.password, 10);

      // Crie o usu√°rio como inativo (aguardando aprova√ß√£o do administrador)
      const user = await storage.createUser({
        ...validatedData,
        password: hashedPassword,
        isActive: false // Usu√°rio precisa ser ativado por um administrador
      });

      res.status(201).json({ 
        ...user, 
        password: '***',
        message: "Cadastro realizado com sucesso. Aguarde a aprova√ß√£o de um administrador para acessar o sistema."
      });
    } catch (error: any) {
      res.status(400).json({ message: "Dados de usu√°rio inv√°lidos", error: error.message });
    }
  });

  // Middleware to check if user is authenticated
  const isAuthenticated = (req: Request, res: Response, next: Function) => {
    if (req.isAuthenticated()) {
      return next();
    }
    res.status(401).json({ message: "N√£o autorizado" });
  };

  // Middleware to check if user is admin
  const isAdmin = (req: Request, res: Response, next: Function) => {
    if (req.isAuthenticated() && (req.user as any)?.role === 'admin') {
      return next();
    }
    res.status(403).json({ message: "Acesso proibido" });
  };



  // User routes
  app.get('/api/users', isAuthenticated, async (req, res) => {
    try {
      const users = await storage.getUsers();
      res.json(users);
    } catch (error) {
      res.status(500).json({ message: "Erro ao buscar usu√°rios", error });
    }
  });

  app.get('/api/users/:id', isAuthenticated, async (req, res) => {
    try {
      const user = await storage.getUser(parseInt(req.params.id));
      if (!user) {
        return res.status(404).json({ message: "Usu√°rio n√£o encontrado" });
      }
      res.json({ ...user, password: '***' });
    } catch (error) {
      res.status(500).json({ message: "Erro ao buscar usu√°rio", error });
    }
  });

  app.post('/api/users', isAdmin, async (req, res) => {
    try {
      const validatedData = insertUserSchema.parse(req.body);
      const user = await storage.createUser(validatedData);
      res.status(201).json({ ...user, password: '***' });
    } catch (error) {
      res.status(400).json({ message: "Dados de usu√°rio inv√°lidos", error });
    }
  });

  app.patch('/api/users/:id', isAdmin, async (req, res) => {
    try {
      const id = parseInt(req.params.id);
      const userData = req.body;
      const updatedUser = await storage.updateUser(id, userData);

      if (!updatedUser) {
        return res.status(404).json({ message: "Usu√°rio n√£o encontrado" });
      }

      res.json(updatedUser);
    } catch (error) {
      res.status(400).json({ message: "Erro ao atualizar usu√°rio", error });
    }
  });

  app.delete('/api/users/:id', isAdmin, async (req, res) => {
    try {
      const id = parseInt(req.params.id);

      // Verificar se √© o usu√°rio administrador padr√£o
      if (id === 1) {
        return res.status(403).json({ message: "N√£o √© poss√≠vel excluir o usu√°rio administrador padr√£o" });
      }

      const user = await storage.getUser(id);
      if (!user) {
        return res.status(404).json({ message: "Usu√°rio n√£o encontrado" });
      }

      // Implementar a exclus√£o do usu√°rio
      await storage.deleteUser(id);

      res.status(200).json({ message: "Usu√°rio exclu√≠do com sucesso" });
    } catch (error) {
      console.error("Erro ao excluir usu√°rio:", error);
      res.status(500).json({ message: "Erro ao excluir usu√°rio", error });
    }
  });

  // Department routes
  app.get('/api/departments', isAuthenticated, async (req, res) => {
    try {
      const departments = await storage.getDepartments();
      res.json(departments);
    } catch (error) {
      res.status(500).json({ message: "Erro ao buscar departamentos", error });
    }
  });

  app.post('/api/departments', isAdmin, async (req, res) => {
    try {
      const validatedData = insertDepartmentSchema.parse(req.body);
      const department = await storage.createDepartment(validatedData);
      res.status(201).json(department);
    } catch (error) {
      res.status(400).json({ message: "Dados de departamento inv√°lidos", error });
    }
  });

  // Bidding modality routes
  app.get('/api/modalities', isAuthenticated, async (req, res) => {
    try {
      const modalities = await storage.getBiddingModalities();
      res.json(modalities);
    } catch (error) {
      res.status(500).json({ message: "Erro ao buscar modalidades", error });
    }
  });

  app.post('/api/modalities', isAdmin, async (req, res) => {
    try {
      const validatedData = insertBiddingModalitySchema.parse(req.body);
      const modality = await storage.createBiddingModality(validatedData);
      res.status(201).json(modality);
    } catch (error) {
      res.status(400).json({ message: "Dados de modalidade inv√°lidos", error });
    }
  });

  // Resource source routes
  app.get('/api/sources', isAuthenticated, async (req, res) => {
    try {
      const sources = await storage.getResourceSources();
      res.json(sources);
    } catch (error) {
      res.status(500).json({ message: "Erro ao buscar fontes de recurso", error });
    }
  });

  // Rota alternativa para manter consist√™ncia com o nome do modelo
  app.get('/api/resource-sources', isAuthenticated, async (req, res) => {
    try {
      const sources = await storage.getResourceSources();
