                      <label class="info-label">Status:</label>
                      <p class="info-value">${process.status === 'in_progress' ? 'Em Andamento' : process.status === 'completed' ? 'Conclu√≠do' : process.status === 'overdue' ? 'Atrasado' : 'Cancelado'}</p>
                    </div>
                    <div class="info-item">
                      <label class="info-label">Modalidade:</label>
                      <p class="info-value">${modality?.name || 'N√£o informado'}</p>
                    </div>
                    <div class="info-item">
                      <label class="info-label">Prioridade:</label>
                      <p class="info-value">${process.priority === 'high' ? 'Alta' : process.priority === 'medium' ? 'M√©dia' : 'Baixa'}</p>
                    </div>
                    <div class="info-item">
                      <label class="info-label">Fonte de Recurso:</label>
                      <p class="info-value">${source ? `${source.code} - ${source.description}` : 'N√£o informado'}</p>
                    </div>
                    <div class="info-item">
                      <label class="info-label">Respons√°vel:</label>
                      <p class="info-value">${responsible?.fullName || 'N√£o informado'}</p>
                    </div>
                    <div class="info-item">
                      <label class="info-label">Departamento Atual:</label>
                      <p class="info-value">${currentDepartment?.name || 'N√£o informado'}</p>
                    </div>
                    <div class="info-item">
                      <label class="info-label">Central de Compras:</label>
                      <p class="info-value">${process.centralDeCompras || 'N√£o informado'}</p>
                    </div>
                  </div>
                </div>

                <div class="separator"></div>

                <!-- Objeto -->
                <div class="section">
                  <h2 class="section-title">Objeto</h2>
                  <div class="description">${process.description}</div>
                </div>

                <div class="separator"></div>

                <!-- Datas -->
                <div class="section">
                  <h2 class="section-title">Cronograma</h2>
                  <div class="info-grid">
                    <div class="info-item">
                      <label class="info-label">Data de Cria√ß√£o:</label>
                      <p class="info-value">${new Date(process.createdAt).toLocaleDateString('pt-BR')} √†s ${new Date(process.createdAt).toLocaleTimeString('pt-BR')}</p>
                    </div>
                    <div class="info-item">
                      <label class="info-label">√öltima Atualiza√ß√£o:</label>
                      <p class="info-value">${new Date(process.updatedAt).toLocaleDateString('pt-BR')} √†s ${new Date(process.updatedAt).toLocaleTimeString('pt-BR')}</p>
                    </div>
                    <div class="info-item">
                      <label class="info-label">Prazo de Entrega:</label>
                      <p class="info-value">${process.deadline ? new Date(process.deadline).toLocaleDateString('pt-BR') : 'N√£o definido'}</p>
                    </div>
                  </div>
                </div>

                ${process.returnComments ? `
                <div class="separator"></div>
                <div class="section">
                  <h2 class="section-title" style="color: #991b1b; border-bottom-color: #fecaca;">Coment√°rios de Retorno</h2>
                  <div class="comments-section">
                    <p class="comments-content">${process.returnComments}</p>
                  </div>
                </div>
                ` : ''}

                ${steps.length > 0 ? `
                <div class="separator"></div>
                <div class="section">
                  <h2 class="section-title">Hist√≥rico de Etapas</h2>
                  <div class="steps-container">
                    ${steps.map((step, index) => {
                      const stepUser = users.find(u => u.id === step.completedBy);
                      const stepDepartment = departments.find(d => d.id === step.departmentId);

                      return `
                        <div class="step-container">
                          <div class="step-header">
                            <h3 class="step-title">${index + 1}. ${step.stepName}</h3>
                            <span class="step-badge ${step.isCompleted ? 'completed' : 'pending'}">
                              ${step.isCompleted ? 'Conclu√≠da' : 'Em andamento'}
                            </span>
                          </div>
                          <div class="step-details">
                            <p><strong>Departamento:</strong> ${stepDepartment?.name || 'N√£o informado'}</p>
                            <p><strong>Respons√°vel:</strong> ${stepUser?.fullName || 'N√£o informado'}</p>
                            ${step.completedAt ? `<p><strong>Data de Conclus√£o:</strong> ${new Date(step.completedAt).toLocaleDateString('pt-BR')} √†s ${new Date(step.completedAt).toLocaleTimeString('pt-BR')}</p>` : ''}
                            ${step.observations ? `<p><strong>Observa√ß√µes:</strong> ${step.observations}</p>` : ''}
                          </div>
                        </div>
                      `;
                    }).join('')}
                  </div>
                </div>
                ` : ''}

                ${responsibilityHistory.length > 0 ? `
                <div class="separator"></div>
                <div class="section">
                  <h2 class="section-title">Hist√≥rico de Responsabilidades</h2>
                  <div class="steps-container">
                    ${responsibilityHistory.map((item, index) => {
                      const actionLabel = item.action === 'transferred' ? 'Transferido' : 
                                         item.action === 'returned' ? 'Devolvido' : 
                                         item.action === 'created' ? 'Criado' : 'A√ß√£o';

                      return `
                        <div class="step-container">
                          <div class="step-header">
                            <h3 class="step-title">${index + 1}. ${actionLabel}</h3>
                            <span class="step-badge completed">
                              ${new Date(item.timestamp).toLocaleDateString('pt-BR')}
                            </span>
                          </div>
                          <div class="step-details">
                            <p><strong>Departamento:</strong> ${item.department_name || 'N√£o informado'}</p>
                            <p><strong>Respons√°vel:</strong> ${item.user_name || 'N√£o informado'}</p>
                            <p><strong>Data:</strong> ${new Date(item.timestamp).toLocaleDateString('pt-BR')} √†s ${new Date(item.timestamp).toLocaleTimeString('pt-BR')}</p>
                            ${item.description ? `<p><strong>Descri√ß√£o:</strong> ${item.description}</p>` : ''}
                          </div>
                        </div>
                      `;
                    }).join('')}
                  </div>
                </div>
                ` : ''}

                <div class="separator"></div>

                <div class="footer">
                  <p>Relat√≥rio gerado em ${new Date().toLocaleDateString('pt-BR')} √†s ${new Date().toLocaleTimeString('pt-BR')}</p>
                  <p>SEAP - Secretaria de Estado da Administra√ß√£o Penitenci√°ria da Para√≠ba</p>
                </div>
              </div>
            </div>
          </div>
        </body>
        </html>
      `;

      // Retornar HTML para que o frontend possa converter em PDF
      res.setHeader('Content-Type', 'text/html; charset=utf-8');
      res.setHeader('Content-Disposition', 'inline');
      res.send(htmlContent);
    } catch (error) {
      console.error('Erro ao gerar relat√≥rio PDF:', error);
      res.status(500).json({ message: "Erro ao gerar relat√≥rio PDF", error: (error as Error).message });
    }
  });

  const httpServer = createServer(app);

  // Configurar WebSocket Server para atualiza√ß√µes em tempo real
  const wss = new WebSocketServer({ server: httpServer, path: '/ws' });

  wss.on('connection', (ws) => {
    console.log('Nova conex√£o WebSocket estabelecida');

    // Adicionar cliente √† lista
    clients.add(ws);

    // Enviar mensagem de boas-vindas
    ws.send(JSON.stringify({ type: 'connection', message: 'Conectado com sucesso!' }));

    ws.on('message', (message) => {
      try {
        const data = JSON.parse(message.toString());
        console.log('Mensagem recebida:', data);
      } catch (error) {
        console.error('Erro ao processar mensagem:', error);
      }
    });

    ws.on('close', () => {
      console.log('Conex√£o WebSocket fechada');
      clients.delete(ws);
    });
  });

  return httpServer;
}

