  async getTemporalDistribution(filters?: {
    pbdocNumber?: string;
    modalityId?: number;
    sourceId?: number;
    responsibleId?: number;
    status?: string;
  }, period?: string): Promise<{
    period: string;
    inProgress: number;
    overdue: number;
    completed: number;
  }[]> {
    console.log('getTemporalDistribution - Filtros recebidos:', filters);
    console.log('getTemporalDistribution - Per√≠odo:', period);

    try {
      // Construir query base
      let query = db
        .select({
          createdAt: processes.createdAt,
          status: processes.status,
          deadline: processes.deadline,
        })
        .from(processes);

      // Aplicar filtros
      const conditions = [];

      // SEMPRE excluir processos na lixeira
      conditions.push(isNull(processes.deletedAt));

      if (filters?.pbdocNumber) {
        conditions.push(like(processes.pbdocNumber, `%${filters.pbdocNumber}%`));
      }
      if (filters?.modalityId) {
        conditions.push(eq(processes.modalityId, filters.modalityId));
      }
      if (filters?.sourceId) {
        conditions.push(eq(processes.sourceId, filters.sourceId));
      }
      if (filters?.responsibleId) {
        conditions.push(eq(processes.responsibleId, filters.responsibleId));
      }
      if (filters?.status) {
        conditions.push(eq(processes.status, filters.status));
      }

      if (conditions.length > 0) {
        query = query.where(and(...conditions));
      }

      const results = await query;
      console.log('getTemporalDistribution - Resultados brutos:', results.length);

      // Agrupar por per√≠odo (m√™s/ano)
      const grouped = new Map<string, {
        inProgress: number;
        overdue: number;
        completed: number;
      }>();

      const now = new Date();

      results.forEach(process => {
        const date = new Date(process.createdAt);
        const periodKey = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}`;

        if (!grouped.has(periodKey)) {
          grouped.set(periodKey, {
            inProgress: 0,
            overdue: 0,
            completed: 0
          });
        }

        const stats = grouped.get(periodKey)!;

        // Classificar status
        if (process.status === 'completed') {
          stats.completed++;
        } else if (process.status === 'overdue' || (process.deadline && new Date(process.deadline) < now)) {
          stats.overdue++;
        } else {
          stats.inProgress++;
        }
      });

      // Converter para array ordenado
      const temporalData = Array.from(grouped.entries())
        .map(([period, stats]) => ({
          period,
          ...stats
        }))
        .sort((a, b) => a.period.localeCompare(b.period));

      console.log('getTemporalDistribution - Dados finais:', temporalData);
      return temporalData;

    } catch (error) {
      console.error('Erro em getTemporalDistribution:', error);
      return [];
    }
  }

  async getDepartmentRanking(filters?: {
    pbdocNumber?: string;
    modalityId?: number;
    sourceId?: number;
    responsibleId?: number;
    status?: string;
  }): Promise<{
    departmentId: number;
    departmentName: string;
    total: number;
    inProgress: number;
    overdue: number;
    completed: number;
  }[]> {
    console.log('getDepartmentRanking - Filtros recebidos:', filters);

    try {
      // Construir query base
      let query = db
        .select({
          currentDepartmentId: processes.currentDepartmentId,
          status: processes.status,
          deadline: processes.deadline,
        })
        .from(processes);

      // Aplicar filtros
      const conditions = [];

      // SEMPRE excluir processos na lixeira
      conditions.push(isNull(processes.deletedAt));

      if (filters?.pbdocNumber) {
        conditions.push(like(processes.pbdocNumber, `%${filters.pbdocNumber}%`));
      }
      if (filters?.modalityId) {
        conditions.push(eq(processes.modalityId, filters.modalityId));
      }
      if (filters?.sourceId) {
        conditions.push(eq(processes.sourceId, filters.sourceId));
      }
      if (filters?.responsibleId) {
        conditions.push(eq(processes.responsibleId, filters.responsibleId));
      }
      if (filters?.status) {
        conditions.push(eq(processes.status, filters.status));
      }

      if (conditions.length > 0) {
        query = query.where(and(...conditions));
      }

      const results = await query;
      console.log('getDepartmentRanking - Resultados brutos:', results.length);

      // Buscar departamentos
      const departmentsList = await db.select().from(departments);
      const departmentMap = new Map(departmentsList.map(d => [d.id, d.name]));

      // Agrupar por departamento
      const grouped = new Map<number, {
        total: number;
        inProgress: number;
        overdue: number;
        completed: number;
      }>();

      const now = new Date();

      results.forEach(process => {
        const deptId = process.currentDepartmentId;

        if (!grouped.has(deptId)) {
          grouped.set(deptId, {
            total: 0,
            inProgress: 0,
            overdue: 0,
            completed: 0
          });
        }

        const stats = grouped.get(deptId)!;
        stats.total++;

        // Classificar status
        if (process.status === 'completed') {
          stats.completed++;
        } else if (process.status === 'overdue' || (process.deadline && new Date(process.deadline) < now)) {
          stats.overdue++;
        } else {
          stats.inProgress++;
        }
      });

      // Converter para array ordenado
      const rankingData = Array.from(grouped.entries())
        .map(([departmentId, stats]) => ({
          departmentId,
          departmentName: departmentMap.get(departmentId) || 'Departamento n√£o encontrado',
          ...stats
        }))
        .sort((a, b) => b.total - a.total);

      console.log('getDepartmentRanking - Dados finais:', rankingData);
      return rankingData;

    } catch (error) {
      console.error('Erro em getDepartmentRanking:', error);
      return [];
    }
  }

  // Convenio operations - stub implementations
  async getConvenios(): Promise<any[]> {
    return [];
  }

  async getConvenio(id: number): Promise<any | undefined> {
    return undefined;
  }

  async createConvenio(convenio: any): Promise<any> {
    return {};
  }

  async updateConvenio(id: number, convenioData: any): Promise<any | undefined> {
    return undefined;
  }

  async deleteConvenio(id: number): Promise<boolean> {
    return false;
  }

  // Process responsibility history operations
  async addProcessResponsibilityHistory(history: InsertProcessResponsibilityHistory): Promise<ProcessResponsibilityHistory> {
    try {
      console.log(`Tentando adicionar hist√≥rico de responsabilidade:`, history);

      // Verificar se j√° existe um registro muito similar (mesmo processo, usu√°rio, a√ß√£o e descri√ß√£o nos √∫ltimos 5 segundos)
      const recentHistory = await db
        .select()
        .from(processResponsibilityHistory)
        .where(
          and(
            eq(processResponsibilityHistory.processId, history.processId),
            eq(processResponsibilityHistory.userId, history.userId),
            eq(processResponsibilityHistory.action, history.action),
            eq(processResponsibilityHistory.description, history.description)
          )
        )
        .orderBy(desc(processResponsibilityHistory.timestamp))
        .limit(1);

      if (recentHistory.length > 0) {
        const lastRecord = recentHistory[0];
        const timeDiff = Date.now() - new Date(lastRecord.timestamp).getTime();

        // Se h√° um registro id√™ntico nos √∫ltimos 5 segundos, n√£o criar duplicata
        if (timeDiff < 5000) {
          console.log(`Registro duplicado detectado, retornando registro existente`);
          return lastRecord;
        }
      }

      const [created] = await db.insert(processResponsibilityHistory).values(history).returning();
      console.log(`Hist√≥rico de responsabilidade criado com sucesso:`, created);
      return created;
    } catch (error) {
      console.error('Erro ao adicionar hist√≥rico de responsabilidade:', error);
      throw error;
    }
  }

  async getProcessResponsibilityHistory(processId: number): Promise<ProcessResponsibilityHistory[]> {
    return await db
      .select()
      .from(processResponsibilityHistory)
      .where(eq(processResponsibilityHistory.processId, processId))
      .orderBy(processResponsibilityHistory.timestamp);
  }

  async getProcessResponsibilityHistoryWithDetails(processId: number): Promise<any[]> {
    try {
      console.log(`Buscando hist√≥rico detalhado para processo ${processId}`);

      const history = await db
        .select({
          id: processResponsibilityHistory.id,
          processId: processResponsibilityHistory.processId,
          userId: processResponsibilityHistory.userId,
          username: users.username,
          fullName: users.fullName,
          userDepartment: users.department,
          action: processResponsibilityHistory.action,
          description: processResponsibilityHistory.description,
          timestamp: processResponsibilityHistory.timestamp,
          departmentId: processResponsibilityHistory.departmentId,
          departmentName: departments.name,
        })
        .from(processResponsibilityHistory)
        .leftJoin(users, eq(processResponsibilityHistory.userId, users.id))
        .leftJoin(departments, eq(processResponsibilityHistory.departmentId, departments.id))
        .where(eq(processResponsibilityHistory.processId, processId))
        .orderBy(processResponsibilityHistory.timestamp);

      console.log(`Hist√≥rico bruto encontrado:`, history.length);

      // Remove duplicatas baseado em ID √∫nico (mais confi√°vel)
      const uniqueHistory = history.filter((item, index, self) => 
        index === self.findIndex(h => h.id === item.id)
      );

      console.log(`Hist√≥rico ap√≥s remo√ß√£o de duplicatas:`, uniqueHistory.length);

      return uniqueHistory;
    } catch (error) {
      console.error('Erro ao buscar hist√≥rico de responsabilidades:', error);
      return [];
    }
  }

  async getRejectedSteps(): Promise<any[]> {
    try {
      console.log('Buscando etapas rejeitadas...');

      const rejectedSteps = await db
        .select({
          id: processSteps.id,
          processId: processSteps.processId,
          stepName: processSteps.stepName,
          observations: processSteps.observations,
          rejectedAt: processSteps.rejectedAt,
          rejectionStatus: processSteps.rejectionStatus,
          process: {
            pbdocNumber: processes.pbdocNumber,
            description: processes.description,
          },
          department: {
            name: departments.name,
          },
          completedBy: {
            username: users.username,
          },
        })
        .from(processSteps)
        .leftJoin(processes, eq(processSteps.processId, processes.id))
        .leftJoin(departments, eq(processSteps.departmentId, departments.id))
        .leftJoin(users, eq(processSteps.completedBy, users.id))
        .where(
          and(
            eq(processSteps.rejectionStatus, 'rejected_with_approval'),
            isNotNull(processSteps.rejectedAt)
          )
        )
        .orderBy(desc(processSteps.rejectedAt));

      console.log(`Etapas rejeitadas encontradas: ${rejectedSteps.length}`);

      return rejectedSteps;
    } catch (error) {
      console.error('Erro ao buscar etapas rejeitadas:', error);
      return [];
    }
  }
}

export const storage = new DatabaseStorage();

