import { pgTable, text, serial, integer, boolean, timestamp, pgEnum, varchar, decimal, date, unique } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

// Enum definitions
export const roleEnum = pgEnum('role', ['common', 'admin']);
export const processStatusEnum = pgEnum('process_status', ['draft', 'in_progress', 'completed', 'canceled', 'overdue']);
export const priorityEnum = pgEnum('priority', ['low', 'medium', 'high']);

// Users table
export const users = pgTable('users', {
  id: serial('id').primaryKey(),
  username: text('username').notNull().unique(),
  password: text('password').notNull(),
  fullName: text('full_name').notNull(),
  email: text('email'),
  department: text('department').notNull(),
  role: roleEnum('role').notNull().default('common'),
  isActive: boolean('is_active').notNull().default(true),
});

// Departments table
export const departments = pgTable('departments', {
  id: serial('id').primaryKey(),
  name: text('name').notNull().unique(),
  description: text('description'),
});

// Bidding modalities table
export const biddingModalities = pgTable('bidding_modalities', {
  id: serial('id').primaryKey(),
  name: text('name').notNull().unique(),
  description: text('description'),
  deadlineDays: integer('deadline_days').notNull().default(7), // Prazo em dias para esta modalidade
});

// Resource sources table
export const resourceSources = pgTable('resource_sources', {
  id: serial('id').primaryKey(),
  code: text('code').notNull().unique(),
  description: text('description'),
});

// Processes table
export const processes = pgTable('processes', {
  id: serial('id').primaryKey(),
  pbdocNumber: text('pbdoc_number').notNull().unique(),
  description: text('description').notNull(),
  modalityId: integer('modality_id').notNull(),
  sourceId: integer('source_id').notNull(),
  responsibleId: integer('responsible_id').notNull(),
  currentDepartmentId: integer('current_department_id'), // Setor atualmente respons√°vel pelo processo
  centralDeCompras: text('central_de_compras'), // N√∫mero do processo da Central de Compras
  priority: priorityEnum('priority').notNull().default('medium'),
  status: processStatusEnum('status').notNull().default('draft'),
  createdAt: timestamp('created_at').notNull().defaultNow(),
  updatedAt: timestamp('updated_at').notNull().defaultNow(),
  responsibleSince: timestamp('responsible_since'), // Data em que o respons√°vel assumiu o processo
  deadline: timestamp('deadline'), // Prazo de entrega do processo
  returnComments: text('return_comments'), // Coment√°rios de retorno do processo
  deletedAt: timestamp('deleted_at'), // Data de exclus√£o (soft delete)
  deletedBy: integer('deleted_by'), // ID do usu√°rio que excluiu
  deletionReason: text('deletion_reason'), // Motivo da exclus√£o
});

// Process steps table
export const processSteps = pgTable('process_steps', {
  id: serial('id').primaryKey(),
  processId: integer('process_id').notNull(),
  stepName: text('step_name').notNull(),
  departmentId: integer('department_id').notNull(),
  isCompleted: boolean('is_completed').notNull().default(false),
  observations: text('observations'),
  completedAt: timestamp('completed_at'),
  completedBy: integer('completed_by'),
  dueDate: timestamp('due_date'),
  rejectedAt: timestamp('rejected_at'), // Data da rejei√ß√£o
  rejectionStatus: text('rejection_status'), // 'rejected_with_approval' para rejei√ß√µes que permitem continuidade
  isLocked: boolean('is_locked').default(false), // Para etapas que ficam bloqueadas at√© serem liberadas por uma decis√£o
  isVisible: boolean('is_visible').notNull().default(true), // Controla se a etapa √© vis√≠vel no frontend
});

// Process participants table - controla quais usu√°rios e setores t√™m acesso a cada processo
export const processParticipants = pgTable('process_participants', {
  id: serial('id').primaryKey(),
  processId: integer('process_id').notNull(),
  userId: integer('user_id').notNull(),
  departmentId: integer('department_id'), // ID do setor participante
  role: text('role').notNull().default('viewer'), // viewer, editor, owner
  addedAt: timestamp('added_at').notNull().defaultNow(),
  isActive: boolean('is_active').notNull().default(true), // Indica se o participante tem acesso ativo
});

// Process responsibility history table
export const processResponsibilityHistory = pgTable('process_responsibility_history', {
  id: serial('id').primaryKey(),
  processId: integer('process_id').notNull(),
  userId: integer('user_id').notNull(),
  action: text('action').notNull(), // 'created', 'updated', 'transferred', 'returned', etc.
  description: text('description'), // Descri√ß√£o da a√ß√£o
  timestamp: timestamp('timestamp').notNull().defaultNow(),
  departmentId: integer('department_id'), // Departamento no momento da a√ß√£o
});

export const convenios = pgTable('convenios', {
  id: serial('id').primaryKey(),
  numero: text('numero').notNull(),
  nome: text('nome').notNull(),
  orgaoConvenente: text('orgao_convenente').notNull(),
  valor: text('valor').notNull(), // Storing as text for simplicity
  dataInicio: text('data_inicio').notNull(), // Storing as text for date
  dataFim: text('data_fim').notNull(), // Storing as text for date
  status: text('status').notNull().default('ativo'),
  observacoes: text('observacoes'),
  createdAt: timestamp('created_at').defaultNow(),
  updatedAt: timestamp('updated_at').defaultNow(),
});

// Export Zod schemas for insert operations
export const insertUserSchema = createInsertSchema(users).omit({
  id: true,
});

export const insertDepartmentSchema = createInsertSchema(departments).omit({
  id: true,
});

export const insertBiddingModalitySchema = createInsertSchema(biddingModalities).omit({
  id: true,
});

export const insertResourceSourceSchema = createInsertSchema(resourceSources).omit({
  id: true,
});

export const insertProcessSchema = createInsertSchema(processes).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

export const insertProcessStepSchema = createInsertSchema(processSteps).omit({
  id: true,
  completedAt: true,
}).extend({
  rejectedAt: z.string().optional(),
  rejectionStatus: z.string().optional(),
});

export const insertProcessParticipantSchema = createInsertSchema(processParticipants).omit({
  id: true,
  addedAt: true,
});

export const insertProcessResponsibilityHistorySchema = createInsertSchema(processResponsibilityHistory).omit({
  id: true,
  timestamp: true,
});

export const insertConvenioSchema = createInsertSchema(convenios).omit({
  id: true,
  createdAt: true,
  updatedAt: true,
});

// Export types
export type User = typeof users.$inferSelect;
export type InsertUser = z.infer<typeof insertUserSchema>;

export type Department = typeof departments.$inferSelect;
export type InsertDepartment = z.infer<typeof insertDepartmentSchema>;

export type BiddingModality = typeof biddingModalities.$inferSelect;
export type InsertBiddingModality = z.infer<typeof insertBiddingModalitySchema>;

export type ResourceSource = typeof resourceSources.$inferSelect;
export type InsertResourceSource = z.infer<typeof insertResourceSourceSchema>;

export type Process = typeof processes.$inferSelect;
export type InsertProcess = z.infer<typeof insertProcessSchema>;

export type ProcessStep = typeof processSteps.$inferSelect;
export type InsertProcessStep = z.infer<typeof insertProcessStepSchema>;

export type ProcessParticipant = typeof processParticipants.$inferSelect;
export type InsertProcessParticipant = z.infer<typeof insertProcessParticipantSchema>;

export type ProcessResponsibilityHistory = typeof processResponsibilityHistory.$inferSelect;
export type InsertProcessResponsibilityHistory = z.infer<typeof insertProcessResponsibilityHistorySchema>;

export type Convenio = typeof convenios.$inferSelect;
export type InsertConvenio = z.infer<typeof insertConvenioSchema>;

// Tabela para processos exclu√≠dos (lixeira)
export const deletedProcesses = pgTable('deleted_processes', {
  id: serial('id').primaryKey(),
  originalProcessId: integer('original_process_id').notNull(),
  pbdocNumber: text('pbdoc_number').notNull(),
  description: text('description').notNull(),
  modalityId: integer('modality_id').notNull(),
  sourceId: integer('source_id').notNull(),
  responsibleId: integer('responsible_id').notNull(),
  currentDepartmentId: integer('current_department_id'),
  centralDeCompras: text('central_de_compras'),
  priority: priorityEnum('priority').notNull(),
  status: processStatusEnum('status').notNull(),
  createdAt: timestamp('created_at').notNull(),
  updatedAt: timestamp('updated_at').notNull(),
  responsibleSince: timestamp('responsible_since'),
  deadline: timestamp('deadline'),
  returnComments: text('return_comments'),
  deletedAt: timestamp('deleted_at').notNull().defaultNow(),
  deletedBy: integer('deleted_by').notNull(),
  deletionReason: text('deletion_reason')
});

export const insertDeletedProcessSchema = createInsertSchema(deletedProcesses);
export type DeletedProcess = typeof deletedProcesses.$inferSelect;
export type InsertDeletedProcess = z.infer<typeof insertDeletedProcessSchema>;

