import { RefreshCw } from "lucide-react";
import { useLocation, Link } from "wouter";
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { User } from "@shared/schema";
import { ThemeToggle } from "./theme-toggle";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import NotificationDropdown from "./notifications/NotificationDropdown";
import { useNotifications } from "@/hooks/use-notifications";

interface HeaderProps {
  title: string;
}

const Header = ({ title }: HeaderProps) => {
  const [location, setLocation] = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const { toast } = useToast();
  const { notifications, unreadCount, markAsRead, markAllAsRead, clearAllNotifications } =
    useNotifications();

  // Get current user data
  const { data: user } = useQuery<User>({
    queryKey: ["/api/auth/status"],
    refetchOnWindowFocus: false,
  });

  // Fun√ß√£o para atualizar todos os dados
  const refreshData = () => {
    // Invalidar as consultas mais comuns para for√ßar a atualiza√ß√£o
    queryClient.invalidateQueries({ queryKey: ["/api/processes"] });
    queryClient.invalidateQueries({
      queryKey: ["/api/analytics/process-statistics"],
    });
    queryClient.invalidateQueries({
      queryKey: ["/api/analytics/processes-by-source"],
    });
    queryClient.invalidateQueries({
      queryKey: ["/api/analytics/processes-by-responsible"],
    });
    queryClient.invalidateQueries({ queryKey: ["/api/settings/monthly-goal"] });
    queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    queryClient.invalidateQueries({ queryKey: ["/api/modalities"] });
    queryClient.invalidateQueries({ queryKey: ["/api/sources"] });

    // Mostrar notifica√ß√£o de dados atualizados
    toast({
      title: "Dados atualizados",
      description: "Todas as informa√ß√µes foram atualizadas com sucesso.",
      duration: 3000,
    });
  };

  // Get user initials for the avatar
  const userInitials = user?.fullName
    ? user.fullName
        .split(" ")
        .map((n) => n[0])
        .slice(0, 2)
        .join("")
        .toUpperCase()
    : "US";

  return (
    <header className="bg-background shadow-sm">
      <div className="flex justify-between items-center px-4 py-3">
        <div className="flex items-center">
          {/* Mobile Menu Button */}
          <button
            type="button"
            className="md:hidden text-secondary-500 mr-3"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M4 6h16M4 12h16M4 18h16"
              />
            </svg>
          </button>

          <div className="text-lg font-medium text-secondary-500 dark:text-white">
            {title}
          </div>

          {/* Bot√£o de atualiza√ß√£o */}
          <Button
            variant="ghost"
            size="sm"
            onClick={refreshData}
            className="ml-3 flex items-center gap-1 h-8"
            title="Atualizar dados"
          >
            <RefreshCw className="h-4 w-4" />
            <span className="hidden sm:inline text-sm">Atualizar</span>
          </Button>
        </div>

        <div className="flex items-center">
          <NotificationDropdown
            notifications={notifications}
            unreadCount={unreadCount}
            onMarkAllAsRead={markAllAsRead}
            onClearAll={clearAllNotifications}
            onReadNotification={markAsRead}
          />

          <button
            type="button"
            className="ml-3 flex items-center text-sm focus:outline-none"
          >
            <span className="mr-2 hidden md:block text-foreground">
              {user?.fullName}
            </span>
            <div className="w-8 h-8 rounded-full bg-secondary-500 flex items-center justify-center text-white">
              <span>{userInitials}</span>
            </div>
          </button>
        </div>
      </div>

      {/* Mobile Menu */}
      {mobileMenuOpen && (
        <div className="md:hidden bg-background shadow-lg absolute z-10 w-full">
          <nav className="px-4 py-2">
            <ul className="space-y-2">
              <li>
                <Link href="/">
                  <a className="block px-4 py-2 text-sm text-foreground hover:bg-muted rounded-md">
                    Dashboard
                  </a>
                </Link>
              </li>
              <li>
                <Link href="/processes">
                  <a className="block px-4 py-2 text-sm text-foreground hover:bg-muted rounded-md">
                    Processos
                  </a>
                </Link>
              </li>
              {user?.role === "admin" && (
                <li>
                  <Link href="/reports">
                    <a className="block px-4 py-2 text-sm text-foreground hover:bg-muted rounded-md">
                      Relat√≥rios
                    </a>
                  </Link>
                </li>
              )}
              {user?.role === "admin" && (
                <li>
                  <Link href="/users">
                    <a className="block px-4 py-2 text-sm text-foreground hover:bg-muted rounded-md">
                      Usu√°rios
                    </a>
                  </Link>
                </li>
              )}
            </ul>
          </nav>
        </div>
      )}
    </header>
  );
};

export default Header;

