        if (location.includes("new")) return "Novo Processo";
        if (location.split("/").length > 2) return "Detalhes do Processo";
        return "Processos";
      case "bidding-flow":
        return "Fluxo de Licita√ß√£o";
      case "reports":
        return "Relat√≥rios";
      case "users":
        return "Usu√°rios";
      case "settings":
        return "Configura√ß√µes";
      case "rejected-steps":
        return "Etapas Rejeitadas";
      case "downloads":
        return "Downloads de Relat√≥rios";
      default:
