client/src/hooks/use-interval.tsx.content:
import { useEffect, useRef } from 'react';

/**
 * Hook para executar uma fun√ß√£o em intervalos regulares. 
 * Baseado na implementa√ß√£o de Dan Abramov: https://overreacted.io/making-setinterval-declarative-with-react-hooks/
 * 
 * @param callback Fun√ß√£o a ser executada no intervalo
 * @param delay Intervalo em milissegundos (null para pausar)
 */
export function useInterval(callback: () => void, delay: number | null) {
  const savedCallback = useRef<() => void>(callback);

  // Salva a √∫ltima callback
  useEffect(() => {
    savedCallback.current = callback;
  }, [callback]);

  // Configura o intervalo
  useEffect(() => {
    function tick() {
      savedCallback.current();
    }

    if (delay !== null) {
      const id = setInterval(tick, delay);
      return () => clearInterval(id);
    }
  }, [delay]);
}

client/src/hooks/use-mobile.tsx.content:
import * as React from "react"

const MOBILE_BREAKPOINT = 768

export function useIsMobile() {
  const [isMobile, setIsMobile] = React.useState<boolean | undefined>(undefined)

  React.useEffect(() => {
    const mql = window.matchMedia(`(max-width: ${MOBILE_BREAKPOINT - 1}px)`)
    const onChange = () => {
      setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    }
    mql.addEventListener("change", onChange)
    setIsMobile(window.innerWidth < MOBILE_BREAKPOINT)
    return () => mql.removeEventListener("change", onChange)
  }, [])

  return !!isMobile
}

client/src/hooks/use-notifications.tsx.content:
import React, { createContext, useContext, useState, useEffect, ReactNode, useRef } from "react";
import { 
  Notification, 
  NotificationState, 
  NotificationType 
} from "@/types/notification";
import { generateId } from "@/lib/utils";
import { useToast } from "@/hooks/use-toast";
import { useInterval } from "./use-interval";
import { useQuery } from "@tanstack/react-query";
import { useLocation } from "wouter";

// API simulada - no futuro pode ser substitu√≠da por chamadas reais √† API
const MOCK_NOTIFICATIONS: Notification[] = [];

// Intervalo para verificar novos processos com prazo pr√≥ximo (15 minutos)
const CHECK_DEADLINES_INTERVAL = 15 * 60 * 1000;

// Contexto para gerenciar o estado das notifica√ß√µes
interface NotificationContextType {
  notifications: Notification[];
  unreadCount: number;
  markAsRead: (id: string) => void;
  markAllAsRead: () => void;
  clearAllNotifications: () => void;
  addNotification: (notification: Omit<Notification, "id" | "createdAt" | "read">) => void;
}

const NotificationContext = createContext<NotificationContextType | null>(null);

export function NotificationProvider({ children }: { children: ReactNode }) {
  const [state, setState] = useState<NotificationState>({
    notifications: [...MOCK_NOTIFICATIONS],
    unreadCount: MOCK_NOTIFICATIONS.filter(n => !n.read).length
  });

  const { toast } = useToast();

  // Buscar processos
  const { data: processes } = useQuery({
    queryKey: ['/api/processes'],
    refetchOnWindowFocus: false,
  });

  // Estado do WebSocket
  const [socket, setSocket] = useState<WebSocket | null>(null);
  const [isConnected, setIsConnected] = useState(false);

  // Verificar processos com prazo pr√≥ximo
  useInterval(() => {
    checkDeadlines();
  }, CHECK_DEADLINES_INTERVAL);

  // Fun√ß√£o para verificar processos com prazo pr√≥ximo
  const checkDeadlines = () => {
    if (!processes || !Array.isArray(processes)) return;

    const today = new Date();
    const threeDaysFromNow = new Date(today);
    threeDaysFromNow.setDate(today.getDate() + 3);

    // Verificar processos com passos que vencem em at√© 3 dias
    processes.forEach(process => {
      if (process.steps && Array.isArray(process.steps)) {
        process.steps.forEach((step: any) => {
          if (step.dueDate && !step.isCompleted) {
            const dueDate = new Date(step.dueDate);

            if (dueDate <= threeDaysFromNow && dueDate >= today) {
              // Verificar se j√° existe notifica√ß√£o para este passo
              const existingNotification = state.notifications.find(
                n => n.type === "deadline" && n.entityId === step.id
              );

              if (!existingNotification) {
                // Criar nova notifica√ß√£o
                addNotification({
                  title: `Prazo pr√≥ximo: ${process.pbdocNumber}`,
                  message: `O passo "${step.stepName}" vence em ${Math.ceil((dueDate.getTime() - today.getTime()) / (1000 * 60 * 60 * 24))} dias`,
                  type: "deadline",
                  link: `/processes/${process.id}`,
                  entityId: step.id
                });
              }
            }
          }
        });
      }
    });
  };

  // Efeito para carregar notifica√ß√µes salvas no localStorage e configurar WebSocket
  useEffect(() => {
    // Carregar notifica√ß√µes do localStorage
    const savedNotifications = localStorage.getItem('notifications');
    if (savedNotifications) {
      try {
        const parsed = JSON.parse(savedNotifications);

        // Converter strings de data para objetos Date
        const notifications = parsed.notifications.map((n: any) => ({
          ...n,
          createdAt: new Date(n.createdAt)
        }));

        setState({
          notifications,
          unreadCount: notifications.filter((n: Notification) => !n.read).length
        });
      } catch (e) {
        console.error("Erro ao carregar notifica√ß√µes do localStorage:", e);
      }
    }

    // Configurar WebSocket para atualiza√ß√µes em tempo real
    const connectWebSocket = () => {
      try {
        // Determinar o protocolo correto (ws ou wss)
        const protocol = window.location.protocol === 'https:' ? 'wss:' : 'ws:';
        const wsUrl = `${protocol}//${window.location.host}/ws`;

        console.log('Conectando ao WebSocket para notifica√ß√µes:', wsUrl);
        const ws = new WebSocket(wsUrl);

        ws.onopen = () => {
          console.log('Conex√£o WebSocket estabelecida para notifica√ß√µes');
          setIsConnected(true);
          setSocket(ws);
        };

        ws.onclose = () => {
          console.log('Conex√£o WebSocket fechada, tentando reconectar em 5 segundos...');
          setIsConnected(false);
          setSocket(null);

          // Tentar reconectar ap√≥s 5 segundos
          setTimeout(connectWebSocket, 5000);
        };

        ws.onerror = (error) => {
          console.error('Erro na conex√£o WebSocket:', error);
        };

        ws.onmessage = (event) => {
          try {
            const data = JSON.parse(event.data);
            console.log('Mensagem WebSocket recebida:', data);

            // Processar diferentes tipos de mensagens
            switch (data.type) {
              case 'new_process':
                // Adicionar nova notifica√ß√£o para o processo criado
                addNotification({
                  title: "Novo processo criado",
                  message: data.message,
                  type: "new_process",
                  link: `/processes/${data.process.id}`,
                  entityId: data.process.id
                });
                break;

              case 'process_updated':
                // Adicionar notifica√ß√£o para atualiza√ß√£o de processo
                addNotification({
                  title: "Processo atualizado",
                  message: data.message,
                  type: "update",
                  link: `/processes/${data.process.id}`,
                  entityId: data.process.id
                });
                break;

              case 'user_created':
                // Adicionar notifica√ß√£o para novo usu√°rio
                addNotification({
                  title: "Novo usu√°rio registrado (tempo real)",
                  message: data.message,
                  type: "admin",
                  link: "/users"
                });
                break;
            }
          } catch (error) {
            console.error('Erro ao processar mensagem WebSocket:', error);
          }
        };

        return ws;
      } catch (error) {
        console.error('Erro ao criar conex√£o WebSocket:', error);
        return null;
      }
    };

    // Iniciar conex√£o WebSocket
    const ws = connectWebSocket();

    // Verificar prazos na inicializa√ß√£o
    checkDeadlines();

    // Limpar conex√£o ao desmontar
    return () => {
      if (ws) {
        ws.close();
      }
    };
  }, []);

  // Salvar notifica√ß√µes no localStorage quando o estado mudar
  useEffect(() => {
    localStorage.setItem('notifications', JSON.stringify({
      notifications: state.notifications
    }));
  }, [state]);

  // Fun√ß√£o para marcar uma notifica√ß√£o como lida
  const markAsRead = (id: string) => {
    setState(prevState => {
      const updatedNotifications = prevState.notifications.map(notification =>
        notification.id === id ? { ...notification, read: true } : notification
      );

      return {
        notifications: updatedNotifications,
        unreadCount: updatedNotifications.filter(n => !n.read).length
      };
    });
  };

  // Fun√ß√£o para marcar todas as notifica√ß√µes como lidas
  const markAllAsRead = () => {
    setState(prevState => ({
      notifications: prevState.notifications.map(notification => ({
        ...notification,
        read: true
      })),
      unreadCount: 0
    }));
  };

  // Fun√ß√£o para limpar todas as notifica√ß√µes
  const clearAllNotifications = () => {
    setState({
      notifications: [],
      unreadCount: 0
    });
    // Limpar tamb√©m do localStorage
    localStorage.removeItem('notifications');
  };

  // Fun√ß√£o para adicionar uma nova notifica√ß√£o
  const addNotification = (notification: Omit<Notification, "id" | "createdAt" | "read">) => {
    const newNotification: Notification = {
      ...notification,
      id: generateId(),
      createdAt: new Date(),
      read: false
    };

    setState(prevState => ({
      notifications: [newNotification, ...prevState.notifications],
      unreadCount: prevState.unreadCount + 1
    }));

    // Mostrar toast para notifica√ß√£o
    toast({
      title: notification.title,
      description: notification.message,
      duration: 5000,
    });
  };

  // Refer√™ncia para armazenar o √∫ltimo count conhecido
  const processCountRef = React.useRef<number>(0);
  const userCountRef = React.useRef<number>(0);

  // Buscar usu√°rios
  const { data: users } = useQuery({
    queryKey: ['/api/users'],
    refetchOnWindowFocus: false,
  });

  // Adicionar observadores para eventos no sistema
  useEffect(() => {
    if (!processes || !Array.isArray(processes)) return;

    const currentCount = processes.length;

    // Verificar se √© a primeira carga
    if (processCountRef.current === 0) {
      processCountRef.current = currentCount;
      return;
    }

    // Se temos mais processos do que antes, novos foram adicionados
    if (currentCount > processCountRef.current) {
      // Encontrar os novos processos
      const newProcesses = processes
        .slice(0, currentCount - processCountRef.current)
        .sort((a, b) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime());

      // Gerar notifica√ß√µes para novos processos
      newProcesses.forEach((process: any) => {
        addNotification({
          title: "Novo processo criado",
          message: `O processo ${process.pbdocNumber} foi adicionado ao sistema`,
          type: "new_process",
          link: `/processes/${process.id}`,
          entityId: process.id
        });
      });
    }

    // Atualizar a refer√™ncia
    processCountRef.current = currentCount;
  }, [processes]);

  // Monitorar mudan√ßas em usu√°rios
  useEffect(() => {
    if (!users || !Array.isArray(users)) return;

    const currentCount = users.length;

    // Verificar se √© a primeira carga
    if (userCountRef.current === 0) {
      userCountRef.current = currentCount;
      return;
    }

    // Se temos mais usu√°rios do que antes, novos foram adicionados
    if (currentCount > userCountRef.current) {
      // Encontrar os novos usu√°rios, presumindo que est√£o no come√ßo do array
      const newUsers = users
        .slice(0, currentCount - userCountRef.current)
        .sort((a, b) => b.id - a.id); // Presumindo que IDs mais altos s√£o mais recentes

      // Gerar notifica√ß√µes para novos usu√°rios
      newUsers.forEach((user: any) => {
        addNotification({
          title: "Novo usu√°rio registrado",
          message: `${user.fullName} (${user.username}) se registrou no sistema`,
          type: "admin",
          link: `/users`,
        });
      });
    }

    // Atualizar a refer√™ncia
    userCountRef.current = currentCount;
  }, [users]);

  return (
    <NotificationContext.Provider
      value={{
        notifications: state.notifications,
        unreadCount: state.unreadCount,
        markAsRead,
        markAllAsRead,
        clearAllNotifications,
        addNotification
      }}
    >
      {children}
    </NotificationContext.Provider>
  );
}

// Extraindo o hook em uma vari√°vel constante para evitar problemas com Fast Refresh
const useNotifications = () => {
  const context = useContext(NotificationContext);

  if (!context) {
    throw new Error("useNotifications deve ser usado dentro de um NotificationProvider");
  }

  return context;
}

export { useNotifications };

