import { useState, useEffect } from "react";
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { apiRequest } from "@/lib/queryClient";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Plus, Eye, Edit, Trash2 } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Textarea } from "@/components/ui/textarea";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import { useToast } from "@/hooks/use-toast";

interface Convenio {
  id: number;
  numero: string;
  nome: string;
  orgaoConvenente: string;
  valor: string;
  dataInicio: string;
  dataFim: string;
  status: string;
  observacoes?: string;
  createdAt: string;
  updatedAt: string;
}

const Convenios = () => {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [editingConvenio, setEditingConvenio] = useState<Convenio | null>(null);
  const [searchTerm, setSearchTerm] = useState("");
  // Carregar conv√™nios salvos do localStorage
  const [convenios, setConvenios] = useState<Convenio[]>(() => {
    try {
      const saved = localStorage.getItem("convenios-seap");
      if (saved) {
        return JSON.parse(saved);
      }
    } catch (error) {
      console.error("Erro ao carregar conv√™nios:", error);
    }
    // Iniciar com lista vazia se n√£o houver dados salvos
    return [];
  });

  // Salvar conv√™nios no localStorage sempre que a lista mudar
  useEffect(() => {
    localStorage.setItem("convenios-seap", JSON.stringify(convenios));
  }, [convenios]);

  // Fun√ß√£o para limpar todos os conv√™nios
  const handleClearAll = () => {
    setConvenios([]);
    localStorage.removeItem("convenios-seap");
    toast({
      title: "Dados limpos",
      description: "Todos os conv√™nios foram removidos.",
    });
  };

  const [newConvenio, setNewConvenio] = useState({
    numero: "",
    nome: "",
    orgaoConvenente: "",
    valor: "",
    dataInicio: "",
    dataFim: "",
    status: "ativo",
    observacoes: "",
  });

  const filteredConvenios = convenios.filter(
    (convenio) =>
      convenio.nome.toLowerCase().includes(searchTerm.toLowerCase()) ||
      convenio.numero.toLowerCase().includes(searchTerm.toLowerCase()) ||
      convenio.orgaoConvenente.toLowerCase().includes(searchTerm.toLowerCase()),
  );

  const handleAddConvenio = () => {
    // Validate required fields
    if (
      !newConvenio.numero ||
      !newConvenio.nome ||
      !newConvenio.orgaoConvenente ||
      !newConvenio.valor ||
      !newConvenio.dataInicio ||
      !newConvenio.dataFim
    ) {
      toast({
        title: "Erro",
        description: "Todos os campos obrigat√≥rios devem ser preenchidos.",
        variant: "destructive",
      });
      return;
    }

    // Create new convenio with generated ID
    const novoConvenio: Convenio = {
      id: Math.max(...convenios.map((c) => c.id)) + 1,
      numero: newConvenio.numero,
      nome: newConvenio.nome,
      orgaoConvenente: newConvenio.orgaoConvenente,
      valor: newConvenio.valor.startsWith("R$")
        ? newConvenio.valor
        : `R$ ${newConvenio.valor}`,
      dataInicio: newConvenio.dataInicio,
      dataFim: newConvenio.dataFim,
      status: newConvenio.status,
      observacoes: newConvenio.observacoes,
      createdAt: new Date().toISOString(),
      updatedAt: new Date().toISOString(),
    };

    // Add to the list
    setConvenios([...convenios, novoConvenio]);

    toast({
      title: "Conv√™nio adicionado",
      description: `Conv√™nio ${newConvenio.numero} foi criado com sucesso.`,
    });

    // Reset form and close dialog
    setNewConvenio({
      numero: "",
      nome: "",
      orgaoConvenente: "",
      valor: "",
      dataInicio: "",
      dataFim: "",
      status: "ativo",
      observacoes: "",
    });
    setIsAddDialogOpen(false);
  };

  // Fun√ß√£o para editar conv√™nio
  const handleEditConvenio = (convenio: Convenio) => {
    setEditingConvenio(convenio);
    setNewConvenio({
      numero: convenio.numero,
      nome: convenio.nome,
      orgaoConvenente: convenio.orgaoConvenente,
      valor: convenio.valor.replace("R$ ", ""),
      dataInicio: convenio.dataInicio,
      dataFim: convenio.dataFim,
      status: convenio.status,
      observacoes: convenio.observacoes || "",
    });
    setIsEditDialogOpen(true);
  };

  // Fun√ß√£o para salvar edi√ß√£o
  const handleSaveEdit = () => {
    if (!editingConvenio) return;

    // Validate required fields
    if (!newConvenio.numero || !newConvenio.nome || !newConvenio.orgaoConvenente || 
        !newConvenio.valor || !newConvenio.dataInicio || !newConvenio.dataFim) {
      toast({
        title: "Erro",
        description: "Todos os campos obrigat√≥rios devem ser preenchidos.",
        variant: "destructive",
      });
      return;
    }

    const updatedConvenios = convenios.map(conv =>
      conv.id === editingConvenio.id
        ? {
            ...conv,
            numero: newConvenio.numero,
            nome: newConvenio.nome,
            orgaoConvenente: newConvenio.orgaoConvenente,
            valor: newConvenio.valor.startsWith("R$") ? newConvenio.valor : `R$ ${newConvenio.valor}`,
            dataInicio: newConvenio.dataInicio,
            dataFim: newConvenio.dataFim,
            status: newConvenio.status,
            observacoes: newConvenio.observacoes,
            updatedAt: new Date().toISOString(),
          }
        : conv
    );

    setConvenios(updatedConvenios);
    setIsEditDialogOpen(false);
    setEditingConvenio(null);
    setNewConvenio({
      numero: "",
      nome: "",
      orgaoConvenente: "",
      valor: "",
      dataInicio: "",
      dataFim: "",
      status: "ativo",
      observacoes: "",
    });

    toast({
      title: "Conv√™nio atualizado",
      description: `Conv√™nio ${newConvenio.numero} foi atualizado com sucesso.`,
    });
  };

  // Fun√ß√£o para excluir conv√™nio
  const handleDeleteConvenio = (id: number) => {
    if (confirm("Tem certeza que deseja excluir este conv√™nio?")) {
      const updatedConvenios = convenios.filter(conv => conv.id !== id);
      setConvenios(updatedConvenios);

      toast({
        title: "Conv√™nio exclu√≠do",
        description: "O conv√™nio foi removido com sucesso.",
      });
    }
  };

  const getStatusBadgeVariant = (status: string) => {
    switch (status) {
      case "ativo":
        return "default";
      case "encerrado":
        return "secondary";
      case "suspenso":
        return "destructive";
      default:
        return "outline";
    }
  };

  const formatCurrency = (value: string) => {
    return value.startsWith("R$") ? value : `R$ ${value}`;
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString("pt-BR");
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Conv√™nios</h1>
          <p className="text-gray-600 mt-1">Gerencie os conv√™nios da SEAP-PB</p>
        </div>

        <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
          <DialogTrigger asChild>
            <Button className="bg-blue-600 hover:bg-blue-700">
              <Plus className="h-4 w-4 mr-2" />
              Adicionar Conv√™nio
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-2xl">
            <DialogHeader>
              <DialogTitle>Adicionar Novo Conv√™nio</DialogTitle>
            </DialogHeader>
            <div className="grid grid-cols-2 gap-4 py-4">
              <div className="space-y-2">
                <Label htmlFor="numero">N√∫mero do Conv√™nio *</Label>
                <Input
                  id="numero"
                  value={newConvenio.numero}
                  onChange={(e) =>
                    setNewConvenio({ ...newConvenio, numero: e.target.value })
                  }
                  placeholder="Ex: CV001/2025"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="nome">Nome do Conv√™nio *</Label>
                <Input
                  id="nome"
                  value={newConvenio.nome}
                  onChange={(e) =>
                    setNewConvenio({ ...newConvenio, nome: e.target.value })
                  }
                  placeholder="Nome do conv√™nio"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="orgaoConvenente">√ìrg√£o Convenente *</Label>
                <Input
                  id="orgaoConvenente"
                  value={newConvenio.orgaoConvenente}
                  onChange={(e) =>
                    setNewConvenio({
                      ...newConvenio,
                      orgaoConvenente: e.target.value,
                    })
                  }
                  placeholder="√ìrg√£o convenente"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="valor">Valor *</Label>
                <Input
                  id="valor"
                  value={newConvenio.valor}
                  onChange={(e) =>
                    setNewConvenio({ ...newConvenio, valor: e.target.value })
                  }
                  placeholder="Ex: 150000.00"
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="dataInicio">Data de In√≠cio *</Label>
                <Input
                  id="dataInicio"
                  type="date"
                  value={newConvenio.dataInicio}
                  onChange={(e) =>
                    setNewConvenio({
                      ...newConvenio,
                      dataInicio: e.target.value,
                    })
                  }
                />
              </div>
              <div className="space-y-2">
                <Label htmlFor="dataFim">Data de Fim *</Label>
                <Input
                  id="dataFim"
