      .min(3, "O nome de usu√°rio deve ter pelo menos 3 caracteres"),
    password: z.string().min(6, "A senha deve ter pelo menos 6 caracteres"),
    confirmPassword: z.string().min(1, "Confirme sua senha"),
    fullName: z.string().min(3, "O nome completo √© obrigat√≥rio"),
    email: z.string().email("Digite um email v√°lido"),
    department: z.string().min(1, "O setor √© obrigat√≥rio"),
  })
  .refine((data) => data.password === data.confirmPassword, {
    message: "As senhas n√£o coincidem",
    path: ["confirmPassword"],
  });

type LoginFormValues = z.infer<typeof loginSchema>;
type RegisterFormValues = z.infer<typeof registerSchema>;

const Login = () => {
  const [isLoading, setIsLoading] = useState(false);
  const [isRegistering, setIsRegistering] = useState(false);
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const [urgentProcesses, setUrgentProcesses] = useState<any[]>([]);
  const [showUrgentAlert, setShowUrgentAlert] = useState(false);
  const [showIntro, setShowIntro] = useState(true);  // Mostrar intro ao carregar a p√°gina

  // Create login form with default values
  const loginForm = useForm<LoginFormValues>({
    resolver: zodResolver(loginSchema),
    defaultValues: {
      username: "",
      password: "",
    },
  });

  // Create register form with default values
  const registerForm = useForm<RegisterFormValues>({
    resolver: zodResolver(registerSchema),
    defaultValues: {
      username: "",
      password: "",
      confirmPassword: "",
      fullName: "",
      email: "",
      department: "",
    },
  });

  const onLoginSubmit = async (data: LoginFormValues) => {
    setIsLoading(true);

    try {
      console.log("Iniciando login para:", data.username);

      // Fazer a requisi√ß√£o diretamente sem usar o helper apiRequest
      console.log("Enviando dados de login:", JSON.stringify(data));
      const response = await fetch("/api/auth/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          "Cache-Control": "no-cache, no-store, must-revalidate",
          "Pragma": "no-cache"
        },
        body: JSON.stringify(data),
        credentials: "include",
        cache: "no-store",
      });

      console.log("Resposta do login:", response.status);

      if (!response.ok) {
        const errorText = await response.text();
        console.error("Detalhes do erro de login:", errorText);
        throw new Error(`Erro no login: ${response.status} - ${errorText}`);
      }

      // Obter dados do usu√°rio
      const user = await response.json();
      console.log("Login bem-sucedido:", user);

      // Verificar processos com prazos pr√≥ximos
      try {
        const processesResponse = await fetch("/api/processes", {
          method: "GET",
          headers: {
            "Content-Type": "application/json",
          },
          credentials: "include",
        });

        if (processesResponse.ok) {
          const processes = await processesResponse.json();

          // Filtrar processos com prazo de at√© 5 dias
          const today = new Date();
          const urgentProcessesList = processes.filter((process: any) => {
            if (!process.deadline) return false;
            const daysRemaining = differenceInDays(new Date(process.deadline), today);
            return daysRemaining >= 0 && daysRemaining <= 5;
          });

          if (urgentProcessesList.length > 0) {
            setUrgentProcesses(urgentProcessesList);
            setShowUrgentAlert(true);
          }
        }
      } catch (error) {
        console.error("Erro ao buscar processos com prazos pr√≥ximos:", error);
      }

      // Show success toast
      toast({
        title: "Login realizado com sucesso",
        description: "Voc√™ ser√° redirecionado para o dashboard",
      });

      console.log("Login bem-sucedido, redirecionando para dashboard");

      // Importar e invalidar o cache do React Query para for√ßar atualiza√ß√£o do estado de auth
      const { queryClient } = await import("../lib/queryClient");
      queryClient.invalidateQueries({ queryKey: ['/api/auth/status'] }); // Remover await para ser mais r√°pido

      // Redirecionar diretamente para o dashboard ap√≥s login
      setLocation("/");
    } catch (error) {
      console.error("Erro no login:", error);

      // Show error toast
      toast({
        title: "Erro ao fazer login",
        description:
          "Verifique seu nome de usu√°rio e senha ou se sua conta foi ativada pelo administrador",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const onRegisterSubmit = async (data: RegisterFormValues) => {
    setIsRegistering(true);

    try {
      // Remove confirmPassword field as it's not needed for the API
      const { confirmPassword, ...userData } = data;

      // Call register API
      const response = await apiRequest("POST", "/api/auth/register", userData);
      const result = await response.json();

      // Show success toast
      toast({
        title: "Cadastro realizado com sucesso",
        description:
          result.message ||
          "Seu cadastro foi enviado para aprova√ß√£o do administrador.",
      });

      // Reset form
      registerForm.reset();

      // Switch to login tab
      document.getElementById("login-tab")?.click();
    } catch (error: any) {
      console.error("Registration error:", error);

      // Show error toast
      toast({
        title: "Erro ao realizar cadastro",
        description:
          error.message || "Verifique os dados informados e tente novamente",
        variant: "destructive",
      });
    } finally {
      setIsRegistering(false);
    }
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-background">
      {/* Alerta de processos com prazos pr√≥ximos */}
      <AlertDialog open={showUrgentAlert} onOpenChange={setShowUrgentAlert}>
        <AlertDialogContent className="max-w-[650px]">
          <AlertDialogHeader>
            <AlertDialogTitle className="flex items-center text-amber-600">
              <AlertTriangle className="mr-2 h-5 w-5" />
              Processos com Prazos Urgentes
            </AlertDialogTitle>
            <AlertDialogDescription>
              Os seguintes processos est√£o com prazos de entrega pr√≥ximos do vencimento e requerem aten√ß√£o imediata:
            </AlertDialogDescription>
          </AlertDialogHeader>
          <div className="max-h-[300px] overflow-y-auto my-4 border rounded-md">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-800 uppercase">PBDOC</th>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-800 uppercase">Objeto</th>
                  <th className="px-4 py-2 text-left text-xs font-medium text-gray-800 uppercase">Prazo</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {urgentProcesses.map((process) => {
                  const daysRemaining = differenceInDays(new Date(process.deadline), new Date());
                  return (
                    <tr key={process.id}>
                      <td className="px-4 py-2 whitespace-nowrap text-sm font-medium">{process.pbdocNumber}</td>
                      <td className="px-4 py-2 text-sm">{process.description}</td>
                      <td className={`px-4 py-2 whitespace-nowrap text-sm font-medium ${
                        daysRemaining <= 2 ? "text-red-600" : "text-amber-600"
                      }`}>
                        {daysRemaining > 0
                          ? `${daysRemaining} dias`
                          : daysRemaining === 0
                          ? "Hoje"
                          : "Vencido"}
                      </td>
                    </tr>
                  );
                })}
              </tbody>
            </table>
          </div>
          <AlertDialogFooter>
