import { useState } from "react";
import { useLocation } from "wouter";
import { useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { InsertProcess } from "@shared/schema";
import ProcessForm from "@/components/process/ProcessForm";

const ProcessCreate = () => {
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const handleSubmit = async (data: InsertProcess) => {
    setIsSubmitting(true);

    try {
      // Pr√©-processar os dados antes de enviar para o servidor
      const processedData = {
        ...data,
        // Garantir que os IDs s√£o n√∫meros
        modalityId: Number(data.modalityId),
        sourceId: Number(data.sourceId),
        responsibleId: Number(data.responsibleId),
        // Todos os processos sempre come√ßam no Setor Demandante (ID: 1)
        currentDepartmentId: 1,
        // Novos processos sempre come√ßam em andamento
        status: "in_progress",
        // Converter deadline string para Date
        deadline: data.deadline ? new Date(data.deadline) : null
      };

      console.log("Dados processados para envio:", processedData);

      // Create the process
      const response = await apiRequest("POST", "/api/processes", processedData);

      if (!response.ok) {
        const errorData = await response.json();
        console.error("Erro detalhado:", errorData);

        let errorMessage = "N√£o foi poss√≠vel criar o processo. Tente novamente.";

        if (errorData.errors) {
          // Formatar os erros para exibi√ß√£o
          const errorDetails = Object.entries(errorData.errors)
            .map(([field, errors]) => `${field}: ${errors}`)
            .join(', ');
          errorMessage = `Erros no formul√°rio: ${errorDetails}`;
        } else if (errorData.error) {
          errorMessage = errorData.error;
        } else if (errorData.message) {
          errorMessage = errorData.message;
        }

        throw new Error(errorMessage);
      }

      const createdProcess = await response.json();

      // Show success message
      toast({
        title: "Processo criado",
        description: "O processo foi criado com sucesso",
      });

      // Invalidate queries to refresh the processes list
      queryClient.invalidateQueries({ queryKey: ['/api/processes'] });

      // Navigate to the process details page
      setLocation(`/processes/${createdProcess.id}`);
    } catch (error) {
      console.error("Error creating process:", error);

      // Show error message
      toast({
        title: "Erro ao criar processo",
        description: error.message || "N√£o foi poss√≠vel criar o processo. Tente novamente.",
        variant: "destructive",
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  return (
    <div className="max-w-5xl mx-auto px-4 py-4">
      <div className="mb-4">
        <h1 className="text-xl md:text-2xl font-semibold text-gray-800">Novo Processo de Licita√ß√£o</h1>
        <p className="text-sm md:text-base text-gray-600 mt-1">Cadastre um novo processo licitat√≥rio no sistema</p>
      </div>

      <ProcessForm 
        onSubmit={handleSubmit} 
        isSubmitting={isSubmitting} 
      />
    </div>
  );
};

export default ProcessCreate;

