import { useState, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { useQuery, useMutation } from "@tanstack/react-query";
import {
  Process,
  BiddingModality,
  ResourceSource,
  User,
  Department,
} from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { DataTable } from "@/components/ui/data-table";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Card, CardContent } from "@/components/ui/card";
import {
  PlusCircle,
  FileText,
  Search,
  SendHorizonal,
  Loader2,
  X,
  Eye,
  AlertTriangle,
} from "lucide-react";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogDescription,
  DialogFooter,
} from "@/components/ui/dialog";
import { Textarea } from "@/components/ui/textarea";
import { getProcessStatusLabel } from "@/lib/utils/process";
import { ColumnDef } from "@tanstack/react-table";
import { getQueryFn, apiRequest, queryClient } from "@/lib/queryClient";
import { toast } from "@/hooks/use-toast";

// Interface para o di√°logo de transfer√™ncia
interface TransferDialogProps {
  isOpen: boolean;
  onOpenChange: (open: boolean) => void;
  processId: number;
  processName: string;
  onTransfer: (departmentId: number) => void;
  isPending: boolean;
}

// Componente do di√°logo de transfer√™ncia
const TransferDialog = ({
  isOpen,
  onOpenChange,
  processId,
  processName,
  onTransfer,
  isPending,
}: TransferDialogProps) => {
  const [selectedDepartment, setSelectedDepartment] = useState<string>("");

  // Buscar departamentos dispon√≠veis
  const { data: departments } = useQuery<Department[]>({
    queryKey: ["/api/departments"],
    queryFn: getQueryFn({ on401: "throw" }),
  });

  const handleTransfer = () => {
    if (!selectedDepartment) {
      toast({
        title: "Erro",
        description: "Selecione um departamento para transferir o processo",
        variant: "destructive",
      });
      return;
    }

    onTransfer(parseInt(selectedDepartment));
  };

  return (
    <Dialog open={isOpen} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle>Transferir Processo</DialogTitle>
          <DialogDescription>
            Selecione o departamento para o qual deseja transferir o processo{" "}
            <strong>{processName}</strong>.
            <br />
            <br />
            <span className="text-yellow-600 font-medium">
              Aten√ß√£o: Ap√≥s a transfer√™ncia, o processo n√£o estar√° mais vis√≠vel
              para voc√™.
            </span>
          </DialogDescription>
        </DialogHeader>

        <div className="grid gap-4 py-4">
          <div className="space-y-2">
            <Select
              onValueChange={setSelectedDepartment}
              value={selectedDepartment}
            >
              <SelectTrigger>
                <SelectValue placeholder="Selecione o departamento destino" />
              </SelectTrigger>
              <SelectContent>
                {departments?.map((dept) => (
                  <SelectItem key={dept.id} value={dept.id.toString()}>
                    {dept.name}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>
        </div>

        <DialogFooter>
          <Button
            variant="outline"
            onClick={() => onOpenChange(false)}
            disabled={isPending}
          >
            Cancelar
          </Button>
          <Button
            onClick={handleTransfer}
            disabled={!selectedDepartment || isPending}
          >
            {isPending ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Transferindo...
              </>
            ) : (
              <>Transferir Processo</>
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
};

// Interface para o di√°logo de exclus√£o
interface DeleteDialogProps {
  isOpen: boolean;
  onOpenChange: (open: boolean) => void;
  processId: number;
  processName: string;
  onDelete: (processId: number, deletionReason: string) => void;
  isPending: boolean;
}

// Componente do di√°logo de exclus√£o
const DeleteDialog = ({
  isOpen,
  onOpenChange,
  processId,
  processName,
  onDelete,
  isPending,
}: DeleteDialogProps) => {
  const [deletionReason, setDeletionReason] = useState("");

  const handleDelete = () => {
    if (!deletionReason.trim()) {
      toast({
        title: "Erro",
        description: "O motivo da exclus√£o √© obrigat√≥rio",
        variant: "destructive",
      });
      return;
    }

    if (deletionReason.trim().length < 10) {
      toast({
        title: "Erro",
        description: "O motivo da exclus√£o deve ter pelo menos 10 caracteres",
        variant: "destructive",
      });
      return;
    }

    onDelete(processId, deletionReason.trim());
  };

  return (
    <Dialog open={isOpen} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[425px]">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <AlertTriangle className="h-5 w-5 text-red-500" />
            Confirmar Exclus√£o
          </DialogTitle>
          <DialogDescription>
            Voc√™ est√° prestes a excluir o processo{" "}
            <strong>{processName}</strong>.
            <br />
            <br />
            <span className="text-red-600 font-medium">
              Aten√ß√£o: O processo ser√° movido para a lixeira e poder√° ser
              recuperado posteriormente.
            </span>
          </DialogDescription>
        </DialogHeader>

        <div className="grid gap-4 py-4">
          <div className="space-y-2">
            <label htmlFor="deletionReason" className="text-sm font-medium">
              Motivo da exclus√£o (obrigat√≥rio)
            </label>
            <Textarea
              id="deletionReason"
              placeholder="Descreva o motivo da exclus√£o do processo (m√≠nimo 10 caracteres)..."
              value={deletionReason}
              onChange={(e) => setDeletionReason(e.target.value)}
              rows={4}
              className="resize-none"
            />
            <div className="text-xs text-gray-500">
              {deletionReason.length}/10 caracteres m√≠nimos
            </div>
          </div>
        </div>

        <DialogFooter>
          <Button
            variant="outline"
            onClick={() => onOpenChange(false)}
            disabled={isPending}
          >
            Cancelar
          </Button>
          <Button
            variant="destructive"
            onClick={handleDelete}
            disabled={
              !deletionReason.trim() ||
              deletionReason.trim().length < 10 ||
              isPending
            }
          >
            {isPending ? (
              <>
                <Loader2 className="mr-2 h-4 w-4 animate-spin" />
                Excluindo...
              </>
            ) : (
              <>Confirmar Exclus√£o</>
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
};

const Processes = () => {
  const [, setLocation] = useLocation();
  const [pbdocFilter, setPbdocFilter] = useState("");
  const [modalityFilter, setModalityFilter] = useState("");
  const [statusFilter, setStatusFilter] = useState("");
  const [sourceFilter, setSourceFilter] = useState("");
  const [responsibleFilter, setResponsibleFilter] = useState("");
  const [centralDeComprasFilter, setCentralDeComprasFilter] = useState("");

  // Estado para controlar o di√°logo de transfer√™ncia
  const [transferDialogOpen, setTransferDialogOpen] = useState(false);
  const [selectedProcess, setSelectedProcess] = useState<Process | null>(null);

  // Refresh autom√°tico ao entrar na p√°gina
  useEffect(() => {
    queryClient.invalidateQueries({ queryKey: ["/api/processes"] });
    queryClient.invalidateQueries({ queryKey: ["/api/modalities"] });
    queryClient.invalidateQueries({ queryKey: ["/api/sources"] });
    queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    queryClient.invalidateQueries({ queryKey: ["/api/departments"] });
  }, []);

  // Estado para controlar o di√°logo de exclus√£o
  const [deleteDialogOpen, setDeleteDialogOpen] = useState(false);
  const [selectedProcessForDelete, setSelectedProcessForDelete] =
    useState<Process | null>(null);

  // Get all processes
  const { data: processes, isLoading: processesLoading } = useQuery<Process[]>({
    queryKey: ["/api/processes"],
  });

  // Get all modalities for filter
  const { data: modalities } = useQuery<BiddingModality[]>({
    queryKey: ["/api/modalities"],
  });

  // Get all sources for filter
  const { data: sources } = useQuery<ResourceSource[]>({
    queryKey: ["/api/sources"],
  });

  // Get all users for filter
  const { data: users } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  // Muta√ß√£o para transferir processo
  const transferMutation = useMutation({
    mutationFn: async ({
      processId,
      departmentId,
    }: {
      processId: number;
      departmentId: number;
    }) => {
      const response = await apiRequest(
        "POST",
        `/api/processes/${processId}/transfer`,
        { departmentId },
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Erro ao transferir processo");
      }

      return response.json();
    },
    onSuccess: () => {
      setTransferDialogOpen(false);

      // Exibe mensagem de sucesso
      toast({
        title: "Processo transferido com sucesso",
        description:
          "O processo foi transferido para outro departamento e n√£o estar√° mais vis√≠vel.",
        variant: "default",
      });

      // Invalida cache para recarregar processos
      queryClient.invalidateQueries({ queryKey: ["/api/processes"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Erro ao transferir processo",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Muta√ß√£o para excluir processo
  const deleteProcessMutation = useMutation({
    mutationFn: async ({
      processId,
      deletionReason,
    }: {
      processId: number;
      deletionReason: string;
    }) => {
      const response = await apiRequest(
        "DELETE",
        `/api/processes/${processId}`,
        { deletionReason },
      );

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || "Erro ao excluir processo");
      }

      return await response.json();
    },
    onSuccess: () => {
      setDeleteDialogOpen(false);
      setSelectedProcessForDelete(null);

      toast({
        title: "Processo exclu√≠do com sucesso",
        description: "O processo foi movido para a lixeira.",
      });

      // Invalidar cache para recarregar processos
      queryClient.invalidateQueries({ queryKey: ["/api/processes"] });
    },
    onError: (error: Error) => {
      toast({
        title: "Erro ao excluir processo",
        description: error.message,
        variant: "destructive",
      });
    },
  });

  // Fun√ß√£o para abrir di√°logo de transfer√™ncia
  const handleTransferClick = (process: Process) => {
    setSelectedProcess(process);
    setTransferDialogOpen(true);
  };

  // Fun√ß√£o para realizar transfer√™ncia
  const handleTransfer = (departmentId: number) => {
    if (!selectedProcess) return;

    transferMutation.mutate({
      processId: selectedProcess.id,
      departmentId,
    });
  };

  // Fun√ß√£o para abrir di√°logo de exclus√£o
  const handleDeleteClick = (process: Process) => {
    setSelectedProcessForDelete(process);
    setDeleteDialogOpen(true);
  };

  // Fun√ß√£o para realizar exclus√£o
  const handleDelete = (processId: number, deletionReason: string) => {
    deleteProcessMutation.mutate({
      processId,
      deletionReason,
    });
  };

  // Filter processes based on selected filters
  const filteredProcesses = processes?.filter((process) => {
    let match = true;

    if (
      pbdocFilter &&
      !process.pbdocNumber.toLowerCase().includes(pbdocFilter.toLowerCase())
    ) {
      match = false;
    }

    if (
      modalityFilter &&
      modalityFilter !== "all" &&
      process.modalityId !== parseInt(modalityFilter)
    ) {
      match = false;
    }

    if (
      statusFilter &&
      statusFilter !== "all" &&
      process.status !== statusFilter
    ) {
      match = false;
    }

    if (
      sourceFilter &&
      sourceFilter !== "all" &&
      process.sourceId !== parseInt(sourceFilter)
    ) {
      match = false;
    }

    if (
      responsibleFilter &&
      responsibleFilter !== "all" &&
      process.responsibleId !== parseInt(responsibleFilter)
    ) {
      match = false;
    }

    if (
      centralDeComprasFilter &&
      process.centralDeCompras &&
      !process.centralDeCompras
        .toLowerCase()
        .includes(centralDeComprasFilter.toLowerCase())
    ) {
      match = false;
    }

    return match;
  });

  const handleViewProcess = (id: number) => {
    setLocation(`/processes/${id}`);
  };

  const columns: ColumnDef<Process>[] = [
    {
      accessorKey: "pbdocNumber",
      header: "PBDOC",
      cell: ({ row }) => (
        <button
          onClick={() => handleViewProcess(row.original.id)}
          className="font-medium text-blue-600 hover:text-blue-800 hover:underline cursor-pointer"
        >
          {row.getValue("pbdocNumber")}
        </button>
      ),
    },
    {
      accessorKey: "description",
      header: "Descri√ß√£o",
      cell: ({ row }) => (
        <div className="max-w-[500px] truncate">
          {row.getValue("description")}
        </div>
      ),
    },
    {
      accessorKey: "modalityId",
      header: "Modalidade",
      cell: ({ row }) => {
        const modalityId = row.getValue("modalityId") as number;
        const modality = modalities?.find((m) => m.id === modalityId);
        return modality?.name || `Modalidade ${modalityId}`;
      },
    },
    {
      accessorKey: "sourceId",
      header: "Fonte",
      cell: ({ row }) => {
        const sourceId = row.getValue("sourceId") as number;
        const source = sources?.find((s) => s.id === sourceId);
        return source ? `Fonte ${source.code}` : `Fonte ${sourceId}`;
      },
    },
    {
      accessorKey: "responsibleId",
      header: "Respons√°vel",
      cell: ({ row }) => {
        const responsibleId = row.getValue("responsibleId") as number;
        const user = users?.find((u) => u.id === responsibleId);
        return user?.fullName || `Usu√°rio ${responsibleId}`;
      },
    },
    {
      accessorKey: "priority",
      header: "Prioridade",
      cell: ({ row }) => {
        const priority = row.getValue("priority") as string;
        return (
          <span className={`priority-badge priority-badge-${priority}`}>
            {priority === "low"
              ? "Baixa"
              : priority === "medium"
                ? "M√©dia"
                : "Alta"}
          </span>
        );
      },
    },
    {
      accessorKey: "status",
      header: "Status",
      cell: ({ row }) => {
        const status = row.getValue("status") as string;
        return (
          <span className={`status-badge status-badge-${status}`}>
            {getProcessStatusLabel(status)}
          </span>
        );
      },
    },
    {
      id: "actions",
      header: "A√ß√µes",
      cell: ({ row }) => {
        const process = row.original;
        return (
          <div className="flex space-x-2">
            <Button
              variant="ghost"
              size="icon"
              title="Visualizar"
              onClick={() => handleViewProcess(process.id)}
            >
              <Eye className="h-5 w-5" />
            </Button>
            <Button
              variant="ghost"
              size="icon"
              title="Excluir"
              className="text-red-500 hover:text-red-700 hover:bg-red-50"
              onClick={() => handleDeleteClick(process)}
            >
              <X className="h-5 w-5" />
            </Button>
          </div>
        );
      },
    },
  ];

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-semibold text-gray-800">
            Processos de Licita√ß√£o
          </h1>
          <p className="text-gray-600">
            Gerencie todos os processos licitat√≥rios
          </p>
        </div>
        <Link href="/processes/new">
          <Button>
            <PlusCircle className="h-4 w-4 mr-2" />
            Novo Processo
          </Button>
        </Link>
      </div>

      {/* Filters */}
      <Card className="mb-6">
        <CardContent className="p-4">
          <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4">
            <div>
              <label
                htmlFor="pbdoc"
                className="block text-sm font-medium text-gray-700 mb-1"
              >
                PBDOC
              </label>
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                <Input
                  id="pbdoc"
                  placeholder="Buscar por PBDOC"
                  className="pl-10"
                  value={pbdocFilter}
                  onChange={(e) => setPbdocFilter(e.target.value)}
                />
              </div>
            </div>

            <div>
              <label
                htmlFor="centralDeCompras"
                className="block text-sm font-medium text-gray-700 mb-1"
              >
                Central de Compras
              </label>
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                <Input
                  id="centralDeCompras"
                  placeholder="N√∫mero Central de Compras"
                  className="pl-10"
                  value={centralDeComprasFilter}
                  onChange={(e) => setCentralDeComprasFilter(e.target.value)}
                />
              </div>
            </div>

            <div>
              <label
                htmlFor="modality"
                className="block text-sm font-medium text-gray-700 mb-1"
              >
                Modalidade
              </label>
              <Select value={modalityFilter} onValueChange={setModalityFilter}>
                <SelectTrigger>
                  <SelectValue placeholder="Todas" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todas</SelectItem>
                  {modalities?.map((modality) => (
                    <SelectItem
                      key={modality.id}
                      value={modality.id.toString()}
                    >
                      {modality.name}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <label
                htmlFor="status"
                className="block text-sm font-medium text-gray-700 mb-1"
              >
                Status
              </label>
              <Select value={statusFilter} onValueChange={setStatusFilter}>
                <SelectTrigger>
                  <SelectValue placeholder="Todos" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todos</SelectItem>
                  <SelectItem value="draft">Rascunho</SelectItem>
                  <SelectItem value="in_progress">Em Andamento</SelectItem>
                  <SelectItem value="completed">Conclu√≠do</SelectItem>
                  <SelectItem value="canceled">Cancelado</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div>
              <label
                htmlFor="source"
                className="block text-sm font-medium text-gray-700 mb-1"
              >
                Fonte
              </label>
              <Select value={sourceFilter} onValueChange={setSourceFilter}>
                <SelectTrigger>
                  <SelectValue placeholder="Todas" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todas</SelectItem>
                  {sources?.map((source) => (
                    <SelectItem key={source.id} value={source.id.toString()}>
                      Fonte {source.code}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <label
                htmlFor="responsible"
                className="block text-sm font-medium text-gray-700 mb-1"
              >
                Respons√°vel
              </label>
              <Select
                value={responsibleFilter}
                onValueChange={setResponsibleFilter}
              >
                <SelectTrigger>
                  <SelectValue placeholder="Todos" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">Todos</SelectItem>
                  {users?.map((user) => (
                    <SelectItem key={user.id} value={user.id.toString()}>
                      {user.fullName}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Process Table */}
      <Card>
        <CardContent className="p-0 sm:p-0">
          {processesLoading ? (
            <div className="p-8 text-center">Carregando processos...</div>
          ) : (
            <DataTable columns={columns} data={filteredProcesses || []} />
          )}
        </CardContent>
      </Card>

      {/* Di√°logo de Transfer√™ncia */}
      {selectedProcess && (
        <TransferDialog
          isOpen={transferDialogOpen}
          onOpenChange={setTransferDialogOpen}
          processId={selectedProcess.id}
          processName={selectedProcess.pbdocNumber}
          onTransfer={handleTransfer}
          isPending={transferMutation.isPending}
        />
      )}

      {/* Di√°logo de Exclus√£o */}
      {selectedProcessForDelete && (
        <DeleteDialog
          isOpen={deleteDialogOpen}
          onOpenChange={setDeleteDialogOpen}
          processId={selectedProcessForDelete.id}
          processName={selectedProcessForDelete.pbdocNumber}
          onDelete={handleDelete}
          isPending={deleteProcessMutation.isPending}
        />
      )}
    </div>
  );
};

export default Processes;

