import { useState, useEffect } from "react";
import { useQuery, useQueryClient } from "@tanstack/react-query";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import { User, Department, insertUserSchema } from "@shared/schema";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { DataTable } from "@/components/ui/data-table";
import { Card, CardContent } from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogFooter,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { PlusCircle, Search, UserPlus, Edit, Trash, UserCheck, UserX } from "lucide-react";
import { ColumnDef } from "@tanstack/react-table";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";

const Users = () => {
  const [nameFilter, setNameFilter] = useState("");
  const [openUserDialog, setOpenUserDialog] = useState(false);
  const [editingUser, setEditingUser] = useState<User | null>(null);

  // Refresh autom√°tico ao entrar na p√°gina
  useEffect(() => {
    queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    queryClient.invalidateQueries({ queryKey: ["/api/departments"] });
  }, []);
  const [deleting, setDeleting] = useState(false);

  const { toast } = useToast();
  const queryClient = useQueryClient();

  // Extended user schema for the form
  const userFormSchema = insertUserSchema
    .extend({
      confirmPassword: z.string().min(1, "Confirme sua senha"),
    })
    .superRefine(({ password, confirmPassword }, ctx) => {
      if (password !== confirmPassword) {
        ctx.addIssue({
          code: "custom",
          message: "As senhas n√£o conferem",
          path: ["confirmPassword"],
        });
      }
    });

  type UserFormValues = z.infer<typeof userFormSchema>;

  const form = useForm<UserFormValues>({
    resolver: zodResolver(userFormSchema),
    defaultValues: {
      username: "",
      password: "",
      confirmPassword: "",
      fullName: "",
      email: "",
      department: "",
      role: "common",
    },
  });

  // Get all users
  const { data: users, isLoading: usersLoading } = useQuery<User[]>({
    queryKey: ["/api/users"],
  });

  // Get all departments
  const { data: departments } = useQuery<Department[]>({
    queryKey: ["/api/departments"],
  });

  // Filter users based on name filter
  const filteredUsers = users?.filter((user) => {
    if (!nameFilter) return true;
    return (
      user.fullName.toLowerCase().includes(nameFilter.toLowerCase()) ||
      user.username.toLowerCase().includes(nameFilter.toLowerCase())
    );
  });

  const openAddUserDialog = () => {
    form.reset({
      username: "",
      password: "",
      confirmPassword: "",
      fullName: "",
      email: "",
      department: "",
      role: "common",
    });
    setEditingUser(null);
    setOpenUserDialog(true);
  };

  const openEditUserDialog = (user: User) => {
    form.reset({
      username: user.username,
      password: "",
      confirmPassword: "",
      fullName: user.fullName,
      email: user.email || "",
      department: user.department,
      role: user.role,
    });
    setEditingUser(user);
    setOpenUserDialog(true);
  };

  const onSubmit = async (data: UserFormValues) => {
    try {
      // Remove confirmPassword as it's not in the API
      const { confirmPassword, ...userData } = data;

      if (editingUser) {
        // If password is empty, don't send it (keep existing)
        const updateData = userData.password
          ? userData
          : { ...userData, password: undefined };

        await apiRequest("PATCH", `/api/users/${editingUser.id}`, updateData);
        toast({
          title: "Usu√°rio atualizado",
          description: "O usu√°rio foi atualizado com sucesso",
        });
      } else {
        await apiRequest("POST", "/api/users", userData);
        toast({
          title: "Usu√°rio criado",
          description: "O usu√°rio foi criado com sucesso",
        });
      }

      // Refresh users list
      queryClient.invalidateQueries({ queryKey: ["/api/users"] });

      // Close dialog
      setOpenUserDialog(false);
    } catch (error) {
      console.error("Error saving user:", error);
      toast({
        title: "Erro",
        description: `Erro ao ${editingUser ? "atualizar" : "criar"} usu√°rio`,
        variant: "destructive",
      });
    }
  };

  const handleDeleteUser = async (userId: number) => {
    setDeleting(true);
    try {
      console.log("Deleting user ID:", userId);

      await apiRequest("DELETE", `/api/users/${userId}`, null);

      toast({
        title: "Usu√°rio exclu√≠do",
        description: "O usu√°rio foi exclu√≠do com sucesso",
      });

      await queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    } catch (error) {
      console.error("Error deleting user:", error);
      toast({
        title: "Erro",
        description: "Erro ao excluir usu√°rio",
        variant: "destructive",
      });
    } finally {
      setDeleting(false);
    }
  };

  const toggleUserActive = async (userId: number, isActive: boolean) => {
    try {
      await apiRequest("PATCH", `/api/users/${userId}`, { 
        isActive: !isActive 
      });

      toast({
        title: isActive ? "Usu√°rio desativado" : "Usu√°rio ativado",
        description: isActive 
          ? "O usu√°rio foi desativado com sucesso" 
          : "O usu√°rio foi ativado e agora pode acessar o sistema",
      });

      await queryClient.invalidateQueries({ queryKey: ["/api/users"] });
    } catch (error) {
      console.error("Error toggling user active status:", error);
      toast({
        title: "Erro",
        description: `Erro ao ${isActive ? "desativar" : "ativar"} usu√°rio`,
        variant: "destructive",
      });
    }
  };

  const columns: ColumnDef<User>[] = [
    {
      accessorKey: "username",
      header: "Usu√°rio",
    },
    {
      accessorKey: "fullName",
      header: "Nome Completo",
    },
    {
      accessorKey: "email",
      header: "Email",
      cell: ({ row }) => row.getValue("email") || "‚Äî",
    },
    {
      accessorKey: "department",
      header: "Setor",
    },
    {
      accessorKey: "role",
      header: "Perfil",
      cell: ({ row }) => {
        const role = row.getValue("role") as string;
        return role === "admin" ? "Administrador" : "Comum";
      },
    },
    {
      accessorKey: "isActive",
      header: "Status",
      cell: ({ row }) => {
        const isActive = row.getValue("isActive") as boolean;
        return (
          <span className={`px-2 py-1 rounded-full text-xs font-medium ${
            isActive 
              ? "bg-green-100 text-green-800" 
              : "bg-orange-100 text-orange-800"
          }`}>
            {isActive ? "Ativo" : "Pendente"}
          </span>
        );
      },
    },
    {
      id: "actions",
      header: "A√ß√µes",
      cell: ({ row }) => {
        const user = row.original;
        return (
          <div className="flex space-x-2">
            <Button
              variant="ghost"
              size="sm"
              onClick={() => openEditUserDialog(user)}
            >
              <Edit className="h-4 w-4" />
            </Button>

            {/* Bot√£o para ativar/desativar usu√°rio */}
            <Button
              variant="ghost"
              size="sm"
              onClick={() => toggleUserActive(user.id, user.isActive)}
              title={user.isActive ? "Desativar usu√°rio" : "Ativar usu√°rio"}
            >
              {user.isActive ? (
                <UserX className="h-4 w-4 text-orange-500" />
              ) : (
                <UserCheck className="h-4 w-4 text-green-500" />
              )}
            </Button>

            <AlertDialog>
              <AlertDialogTrigger asChild>
                <Button variant="ghost" size="sm">
                  <Trash className="h-4 w-4 text-red-500" />
                </Button>
              </AlertDialogTrigger>
              <AlertDialogContent>
                <AlertDialogHeader>
                  <AlertDialogTitle>Confirmar exclus√£o</AlertDialogTitle>
                  <AlertDialogDescription>
                    Tem certeza que deseja excluir o usu√°rio "{user.fullName}"?
                    Esta a√ß√£o n√£o pode ser desfeita.
                  </AlertDialogDescription>
                </AlertDialogHeader>
                <AlertDialogFooter>
                  <AlertDialogCancel>Cancelar</AlertDialogCancel>
                  <AlertDialogAction
                    onClick={() => handleDeleteUser(user.id)}
                    disabled={deleting}
                  >
                    {deleting ? "Excluindo..." : "Excluir"}
                  </AlertDialogAction>
                </AlertDialogFooter>
              </AlertDialogContent>
            </AlertDialog>
          </div>
        );
      },
    },
  ];

  return (
    <div>
      <div className="flex justify-between items-center mb-6">
        <div>
          <h1 className="text-2xl font-semibold text-gray-800">Usu√°rios</h1>
          <p className="text-gray-600">Gerencie os usu√°rios do sistema</p>
        </div>
        <Button onClick={openAddUserDialog}>
          <UserPlus className="h-4 w-4 mr-2" />
          Novo Usu√°rio
        </Button>
      </div>

      {/* Filters */}
      <Card className="mb-6">
        <CardContent className="p-4">
          <div className="flex items-center space-x-2">
            <div className="flex-1">
              <label
                htmlFor="name-filter"
                className="block text-sm font-medium text-gray-700 mb-1"
              >
                Nome ou Usu√°rio
              </label>
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-gray-400" />
                <Input
                  id="name-filter"
                  placeholder="Buscar por nome ou usu√°rio"
                  className="pl-10"
                  value={nameFilter}
                  onChange={(e) => setNameFilter(e.target.value)}
                />
              </div>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Users Table */}
      <Card>
        <CardContent className="p-0 sm:p-0">
          {usersLoading ? (
            <div className="p-8 text-center">Carregando usu√°rios...</div>
          ) : (
            <DataTable columns={columns} data={filteredUsers || []} />
          )}
        </CardContent>
      </Card>

      {/* Add/Edit User Dialog */}
      <Dialog open={openUserDialog} onOpenChange={setOpenUserDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle>
              {editingUser ? "Editar Usu√°rio" : "Novo Usu√°rio"}
            </DialogTitle>
            <DialogDescription>
              {editingUser
                ? "Edite as informa√ß√µes do usu√°rio abaixo"
                : "Preencha as informa√ß√µes para criar um novo usu√°rio"}
            </DialogDescription>
          </DialogHeader>

          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
              <FormField
                control={form.control}
                name="username"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Nome de Usu√°rio</FormLabel>
                    <FormControl>
                      <Input placeholder="usuario" {...field} />
                    </FormControl>
                    <FormDescription>
                      Nome de usu√°rio para login no sistema
                    </FormDescription>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="fullName"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Nome Completo</FormLabel>
                    <FormControl>
                      <Input placeholder="Nome Completo" {...field} />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="password"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>
                        {editingUser ? "Nova Senha" : "Senha"}
                      </FormLabel>
                      <FormControl>
                        <Input
                          type="password"
                          placeholder={
                            editingUser
                              ? "Deixe em branco para manter a atual"
                              : "Senha"
                          }
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="confirmPassword"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Confirmar Senha</FormLabel>
                      <FormControl>
                        <Input
                          type="password"
                          placeholder="Confirme a senha"
                          {...field}
                        />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel>Email</FormLabel>
                    <FormControl>
                      <Input
                        type="email"
                        placeholder="email@exemplo.com"
                        {...field}
                      />
                    </FormControl>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                <FormField
                  control={form.control}
                  name="department"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Setor</FormLabel>
                      <Select
                        onValueChange={field.onChange}
                        defaultValue={field.value}
                      >
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Selecione o setor" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {departments?.map((department) => (
                            <SelectItem
                              key={department.id}
                              value={department.name}
                            >
                              {department.name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="role"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Perfil</FormLabel>
                      <Select
                        onValueChange={field.onChange}
                        defaultValue={field.value}
                      >
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Selecione o perfil" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          <SelectItem value="common">Comum</SelectItem>
                          <SelectItem value="admin">Administrador</SelectItem>
                        </SelectContent>
                      </Select>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <DialogFooter>
                <Button type="submit">
                  {editingUser ? "Salvar Altera√ß√µes" : "Criar Usu√°rio"}
                </Button>
              </DialogFooter>
            </form>
          </Form>
        </DialogContent>
      </Dialog>
    </div>
  );
};

export default Users;

