      await apiRequest(
        "PATCH",
        `/api/processes/${processId}/steps/${activeStep.id}`,
        {
          observations: observation,
        },
      );

      // Refetch steps after updating
      queryClient.invalidateQueries({
        queryKey: [`/api/processes/${processId}/steps`],
      });

      toast({
        title: "Observa√ß√µes salvas",
        description: "As observa√ß√µes foram salvas com sucesso",
      });
    } catch (error) {
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel salvar as observa√ß√µes",
        variant: "destructive",
      });
    }
  };

  // Fun√ß√£o para completar a autoriza√ß√£o com a decis√£o escolhida
  const handleAuthorizationComplete = async () => {
    if (!activeStep || !authorizationDecision) return;

    try {
      // Completar a etapa com a decis√£o de autoriza√ß√£o como observa√ß√£o
      if (!activeStep.id) {
        // Criar etapa se n√£o existe
        await apiRequest("POST", `/api/processes/${processId}/steps`, {
          stepName: activeStep.stepName,
          departmentId: activeStep.departmentId,
          isCompleted: true,
          observations: `AUTORIZA√á√ÉO: ${authorizationDecision}`,
        });
      } else {
        // Atualizar etapa existente
        await apiRequest(
          "PATCH",
          `/api/processes/${processId}/steps/${activeStep.id}`,
          {
            isCompleted: true,
            observations: `AUTORIZA√á√ÉO: ${authorizationDecision}`,
          },
        );
      }

      // Se a decis√£o for "DISPONIBILIDADE OR√áAMENT√ÅRIA", criar a etapa "Autorizar Emiss√£o de R.O"
      if (authorizationDecision === "DISPONIBILIDADE OR√áAMENT√ÅRIA") {
        console.log(
          "üî•üî•üî• StepChecklist - Criando etapa 'Autorizar Emiss√£o de R.O' para disponibilidade or√ßament√°ria",
        );

        try {
          // Verificar se a etapa j√° existe
          const stepsResponse = await apiRequest(
            "GET",
            `/api/processes/${processId}/steps`,
          );
          const currentSteps = await stepsResponse.json();
          const authRoStepExists = currentSteps.find(
            (s: any) => s.stepName === "Autorizar Emiss√£o de R.O",
          );

          if (!authRoStepExists) {
            // Criar etapa "Autorizar Emiss√£o de R.O" no setor SEAP (ID 5)
            console.log(
              "üî•üî•üî• StepChecklist - Criando etapa no departamento SEAP (ID: 5)",
            );
            const authRoResponse = await apiRequest(
              "POST",
              `/api/processes/${processId}/steps`,
              {
                stepName: "Autorizar Emiss√£o de R.O",
                departmentId: 5, // SEAP - Secret√°rio de Estado da Administra√ß√£o Penitenci√°ria
                phase: "Execu√ß√£o",
              },
            );

            if (authRoResponse.ok) {
              console.log(
                "‚úÖ‚úÖ‚úÖ StepChecklist - Etapa 'Autorizar Emiss√£o de R.O' criada com sucesso",
              );
              const createdStep = await authRoResponse.json();
              console.log(
                "üî• StepChecklist - Dados da etapa criada:",
                createdStep,
              );
            } else {
              console.error(
                "‚ùå‚ùå‚ùå StepChecklist - Erro ao criar etapa 'Autorizar Emiss√£o de R.O'",
              );
              const errorData = await authRoResponse.text();
              console.error("üî• StepChecklist - Erro detalhes:", errorData);
            }
          } else {
            console.log(
              "‚úÖ StepChecklist - Etapa 'Autorizar Emiss√£o de R.O' j√° existe",
            );
          }
        } catch (etapasError) {
          console.error(
            "‚ùå StepChecklist - Erro ao verificar/criar etapa:",
            etapasError,
          );
        }
      }

      // Liberar etapa espec√≠fica baseada na decis√£o
      let stepToUnlock = "";
      if (
        authorizationDecision ===
        "INDISPONIBILIDADE OR√áAMENT√ÅRIA TOTAL OU PARCIAL"
      ) {
        stepToUnlock = "Solicitar disponibiliza√ß√£o de or√ßamento";
      } else if (authorizationDecision === "DISPONIBILIDADE OR√áAMENT√ÅRIA") {
        stepToUnlock = "Autorizar emiss√£o de R.O.";
      }

      // Liberar a etapa correspondente
      if (stepToUnlock) {
        const stepToUnlockObj = steps?.find(
          (s) => s.stepName === stepToUnlock && s.departmentId === 5,
        );
        if (stepToUnlockObj) {
          await apiRequest(
            "PATCH",
            `/api/processes/${processId}/steps/${stepToUnlockObj.id}`,
            {
              observations: `Etapa liberada pela decis√£o: ${authorizationDecision}`,
              isLocked: false, // Desbloquear a etapa
            },
          );
        }
      }

      // Refetch steps after updating
      queryClient.invalidateQueries({
        queryKey: [`/api/processes/${processId}/steps`],
      });

      toast({
        title: "Etapa de Autoriza√ß√£o conclu√≠da",
        description: `Decis√£o: ${authorizationDecision}`,
      });

      // Limpar estados e fechar modal
      setAuthorizationModalOpen(false);
      setAuthorizationDecision("");
      setActiveStep(null);
    } catch (error) {
      toast({
        title: "Erro",
        description: "N√£o foi poss√≠vel completar a autoriza√ß√£o",
        variant: "destructive",
      });
    }
  };

  if (isLoading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Checklist de Etapas</CardTitle>
        </CardHeader>
        <CardContent>
          <p>Carregando etapas...</p>
        </CardContent>
      </Card>
    );
  }

  if (error || !steps) {
    return (
      <Card>
        <CardHeader>
          <CardTitle>Checklist de Etapas</CardTitle>
        </CardHeader>
        <CardContent>
          <p className="text-red-500">Erro ao carregar etapas</p>
        </CardContent>
      </Card>
    );
  }

  // Agrupar etapas filtradas por fase
  const stepsByPhase =
    filteredSteps?.reduce(
      (acc, step) => {
        // Extrair fase do nome da etapa ou usar uma fase padr√£o
        let phase = "Execu√ß√£o";
        if (
          step.stepName.includes("DFD") ||
          step.stepName.includes("ETP") ||
          step.stepName.includes("Mapa de Risco") ||
          step.stepName.includes("Termo de Refer√™ncia") ||
          step.stepName.includes("Ordenador")
        ) {
          phase = "Inicia√ß√£o";
        } else if (
          step.stepName.includes("Processo no √ìrg√£o") ||
          step.stepName.includes("Pesquisa") ||
          step.stepName.includes("Or√ßament") ||
          step.stepName.includes("Reserva")
        ) {
          phase = "Prepara√ß√£o";
        } else if (
          step.stepName.includes("Elabora√ß√£o") ||
          step.stepName.includes("Contrato") ||
          step.stepName.includes("Assinatura") ||
          step.stepName.includes("Adjudica") ||
          step.stepName.includes("Homologa")
        ) {
          phase = "Finaliza√ß√£o";
        }

        if (!acc[phase]) acc[phase] = [];
        acc[phase].push(step);
        return acc;
      },
      {} as Record<string, typeof filteredSteps>,
    ) || {};

  const phaseOrder = ["Inicia√ß√£o", "Prepara√ß√£o", "Execu√ß√£o", "Finaliza√ß√£o"];
  const phaseColors = {
    Inicia√ß√£o: "bg-blue-50 border-blue-200",
    Prepara√ß√£o: "bg-yellow-50 border-yellow-200",
    Execu√ß√£o: "bg-green-50 border-green-200",
    Finaliza√ß√£o: "bg-purple-50 border-purple-200",
  };

  // Debug do estado do modal
  console.log(
    "üî• StepChecklist renderizando - authorizationModalOpen:",
    authorizationModalOpen,
  );

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            Checklist - Fluxograma Preg√£o Eletr√¥nico
            <span className="text-sm font-normal text-gray-500">SEAP/PB</span>
          </CardTitle>
          <p className="text-sm text-gray-600">
            Siga as etapas conforme o fluxograma oficial da Lei n¬∫ 14.133/2021
          </p>
        </CardHeader>
        <CardContent>
          {steps.length === 0 ? (
            <p>Nenhuma etapa cadastrada para este processo</p>
          ) : (
            <div className="space-y-6">
              {/* Resumo de etapas conclu√≠das */}
              {(() => {
                const currentDeptId = departmentMap[userDepartment];
                const allSectorSteps =
                  steps?.filter(
                    (step) =>
                      step.departmentId === currentDeptId &&
                      step.stepName !== "Transfer√™ncia de Setor",
                  ) || [];
                const completedSteps = allSectorSteps.filter(
                  (step) => step.isCompleted,
                );

                if (completedSteps.length > 0) {
                  return (
                    <div className="p-3 bg-green-50 border border-green-200 rounded-lg">
                      <div className="flex items-center justify-between">
                        <span className="text-sm font-medium text-green-800">
                          ‚úì {completedSteps.length} de {allSectorSteps.length}{" "}
                          etapas conclu√≠das
                        </span>
                        <span className="text-xs text-green-600">
                          (etapas conclu√≠das ocultas)
                        </span>
                      </div>
                    </div>
                  );
                }
                return null;
              })()}

              {phaseOrder.map((phase) => {
                const phaseSteps = stepsByPhase[phase] || [];
                if (phaseSteps.length === 0) return null;

                return (
                  <div
                    key={phase}
                    className={`p-4 rounded-lg border-2 ${phaseColors[phase as keyof typeof phaseColors]}`}
                  >
                    <h3 className="font-semibold text-sm mb-3 uppercase tracking-wide">
                      {phase}
                    </h3>
                    <div className="space-y-3">
                      {phaseSteps.map((step) => (
                        <div
                          key={step.id}
                          className={`flex items-start space-x-3 p-3 rounded-md border ${
                            step.isLocked && !step.isCompleted
                              ? "bg-gray-200 border-gray-400 opacity-40"
                              : activeStep && activeStep.id === step.id
                                ? "bg-white border-blue-500 shadow-md"
                                : "bg-white border-gray-200"
                          } ${step.isLocked && !step.isCompleted ? "cursor-not-allowed" : "cursor-pointer hover:shadow-sm"} transition-all`}
                          onClick={() => {
                            if (step.isLocked && !step.isCompleted) {
                              toast({
                                title: "‚ùå Etapa Bloqueada",
                                description:
                                  "Esta etapa s√≥ pode ser acessada ap√≥s uma decis√£o na 'Autoriza√ß√£o pelo Secret√°rio SEAP'",
                                variant: "destructive",
                              });
                              return;
                            }
                            handleStepClick(step);
                          }}
                        >
                          <Checkbox
                            id={`step-${step.id}`}
                            checked={step.isCompleted || false}
                            disabled={
                              (step.isLocked || false) && !step.isCompleted
                            }
                            onCheckedChange={async (checked) => {
                              // Primeira valida√ß√£o: bloquear no frontend
                              if (step.isLocked && !step.isCompleted) {
                                toast({
                                  title: "‚ùå Etapa Bloqueada",
                                  description:
                                    "Esta etapa s√≥ pode ser acessada ap√≥s uma decis√£o na 'Autoriza√ß√£o pelo Secret√°rio SEAP'",
                                  variant: "destructive",
                                });
                                return;
                              }

                              // Especial para "Autorizar via sistema" - arquivar automaticamente
                              if (
                                step.stepName === "Autorizar via sistema" &&
                                !step.isCompleted
                              ) {
                                try {
                                  console.log(
                                    "üî• Arquivando processo ao aprovar 'Autorizar via sistema' (checkbox)",
                                  );

                                  // Concluir etapa primeiro
                                  const updateResponse = await apiRequest(
                                    "PATCH",
                                    `/api/processes/${processId}/steps/${step.id}`,
                                    {
                                      isCompleted: true,
                                      observations:
                                        "Autorizado via sistema - Processo arquivado automaticamente",
                                    },
                                  );

                                  if (updateResponse.ok) {
                                    // Arquivar processo automaticamente
                                    const archiveResponse = await apiRequest(
                                      "DELETE",
                                      `/api/processes/${processId}`,
                                      {
                                        deletionReason:
                                          "Autorizado via sistema - Secret√°rio de Estado da Administra√ß√£o Penitenci√°ria - SEAP",
                                      },
                                    );

                                    if (archiveResponse.ok) {
                                      queryClient.invalidateQueries({
                                        queryKey: ["/api/processes"],
                                      });
                                      toast({
                                        title:
                                          "Processo Autorizado e Arquivado",
                                        description:
                                          "Autoriza√ß√£o via sistema conclu√≠da. Processo arquivado automaticamente.",
                                        variant: "default",
                                      });

                                      // Redirecionar para lista de processos
                                      setTimeout(() => {
                                        window.location.href = "/processes";
                                      }, 2000);
                                    } else {
                                      const errorText =
                                        await archiveResponse.text();
                                      toast({
                                        title: "Erro no arquivamento",
                                        description:
                                          "Etapa aprovada mas processo n√£o foi arquivado. Erro: " +
                                          errorText,
                                        variant: "destructive",
                                      });
                                    }
                                  }
                                } catch (error) {
                                  toast({
                                    title: "Erro",
                                    description:
                                      "Erro ao processar autoriza√ß√£o via sistema",
                                    variant: "destructive",
                                  });
                                }
                              } else {
                                // Comportamento padr√£o para outras etapas
                                handleToggleStep(step);
                              }
                            }}
                            onClick={(e) => e.stopPropagation()}
                          />
                          <div className="grid gap-1.5 leading-none flex-1">
                            <Label
                              htmlFor={`step-${step.id}`}
                              className={`text-sm font-medium ${
                                step.isLocked && !step.isCompleted
                                  ? "text-gray-400"
                                  : step.isCompleted
                                    ? "line-through text-gray-500"
                                    : step.observations &&
                                        step.observations.startsWith(
                                          "REJEITADA:",
                                        )
                                      ? "text-red-600"
                                      : "text-gray-800"
                              }`}
                            >
                              {step.stepName}
                              {step.isLocked && !step.isCompleted && (
                                <span className="ml-2 text-xs bg-red-100 text-red-700 px-2 py-1 rounded font-semibold">
                                  üîí BLOQUEADA
                                </span>
                              )}
                            </Label>
                            <div className="flex items-center gap-3 text-xs text-gray-500">
                              <span>
                                Setor:{" "}
                                {departments && Array.isArray(departments)
                                  ? departments.find(
                                      (d: any) => d.id === step.departmentId,
                                    )?.name || `Setor ${step.departmentId}`
                                  : `Setor ${step.departmentId}`}
                              </span>
                              {step.observations &&
                                step.observations.includes("Prazo:") && (
                                  <span className="text-orange-600 font-medium">
                                    {step.observations}
                                  </span>
                                )}
                            </div>
                            {step.completedAt && (
                              <p className="text-xs text-green-600">
                                ‚úì Conclu√≠do em:{" "}
                                {new Date(step.completedAt).toLocaleDateString(
                                  "pt-BR",
                                )}
                              </p>
                            )}
                            {step.observations &&
                              step.observations.startsWith("REJEITADA:") && (
                                <p className="text-xs text-red-600 font-medium">
                                  ‚úó Rejeitada:{" "}
                                  {step.observations.replace("REJEITADA: ", "")}
                                </p>
                              )}
                          </div>
                          <div className="flex flex-row gap-2 items-center">
                            {/* Bot√£o de Aprovar */}
                            <Button
                              size="sm"
                              variant={
                                step.isCompleted ? "secondary" : "default"
                              }
                              onClick={async (e) => {
                                e.stopPropagation();

                                // Especial para "Autorizar via sistema" - arquivar automaticamente
                                if (
                                  step.stepName === "Autorizar via sistema" &&
                                  !step.isCompleted
                                ) {
                                  try {
                                    console.log(
                                      "üî• Arquivando processo ao aprovar 'Autorizar via sistema'",
                                    );

                                    // Concluir etapa primeiro
                                    const updateResponse = await apiRequest(
                                      "PATCH",
                                      `/api/processes/${processId}/steps/${step.id}`,
                                      {
                                        isCompleted: true,
                                        observations:
                                          "Autorizado via sistema - Processo arquivado automaticamente",
                                      },
                                    );

                                    if (updateResponse.ok) {
                                      // Arquivar processo automaticamente
                                      const archiveResponse = await apiRequest(
                                        "DELETE",
                                        `/api/processes/${processId}`,
                                        {
                                          deletionReason:
                                            "Autorizado via sistema - Secret√°rio de Estado da Administra√ß√£o Penitenci√°ria - SEAP",
                                        },
                                      );

                                      if (archiveResponse.ok) {
                                        queryClient.invalidateQueries({
                                          queryKey: ["/api/processes"],
                                        });
                                        toast({
                                          title:
                                            "Processo Autorizado e Arquivado",
                                          description:
                                            "Autoriza√ß√£o via sistema conclu√≠da. Processo arquivado automaticamente.",
                                          variant: "default",
                                        });

                                        // Redirecionar para lista de processos
                                        setTimeout(() => {
                                          window.location.href = "/processes";
                                        }, 2000);
                                      } else {
                                        const errorText =
                                          await archiveResponse.text();
                                        toast({
                                          title: "Erro no arquivamento",
                                          description:
                                            "Etapa aprovada mas processo n√£o foi arquivado. Erro: " +
                                            errorText,
                                          variant: "destructive",
                                        });
                                      }
                                    }
                                  } catch (error) {
                                    toast({
                                      title: "Erro",
                                      description:
                                        "Erro ao processar autoriza√ß√£o via sistema",
                                      variant: "destructive",
                                    });
                                  }
                                } else {
                                  // Comportamento padr√£o para outras etapas
                                  handleToggleStep(step);
                                }
                              }}
                              className="h-8 w-8 p-0"
                              title="Aprovar etapa"
                            >
                              <Check className="h-4 w-4" />
                            </Button>

                            {/* Bot√£o de Rejeitar */}
                            <Button
                              size="sm"
                              variant="destructive"
                              onClick={(e) => {
                                e.stopPropagation();
                                if (onStepReject) {
                                  onStepReject(step);
                                } else {
                                  toast({
                                    title: "Erro",
                                    description:
                                      "Fun√ß√£o de rejei√ß√£o n√£o configurada",
                                    variant: "destructive",
                                  });
                                }
                              }}
                              className="h-8 w-8 p-0"
                              title="Rejeitar etapa"
                            >
                              <XCircle className="h-4 w-4" />
                            </Button>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </CardContent>
      </Card>

      {activeStep && (
        <Card>
          <CardHeader>
            <CardTitle>Observa√ß√µes</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              <div>
                <p className="text-sm font-medium mb-2">
                  Etapa:{" "}
                  {activeStep.stepName === "Autoriza√ß√£o pelo Secret√°rio SEAP"
                    ? "Autoriza√ß√£o"
                    : activeStep.stepName}
                </p>
                <Textarea
                  value={observation}
                  onChange={(e) => setObservation(e.target.value)}
                  placeholder="Adicione observa√ß√µes sobre esta etapa"
                  rows={5}
                />
              </div>
              <Button onClick={saveObservation}>Salvar Observa√ß√µes</Button>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
};

export default StepChecklist;

