import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useQuery } from "@tanstack/react-query";
import { User, BiddingModality, ResourceSource, Department, insertProcessSchema } from "@shared/schema";
import { Button } from "@/components/ui/button";
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Card,
  CardContent,
  CardDescription,
  CardFooter,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  RadioGroup,
  RadioGroupItem,
} from "@/components/ui/radio-group";
import { Label } from "@/components/ui/label";
import { Checkbox } from "@/components/ui/checkbox";

// Extend the process schema for the form
const processFormSchema = insertProcessSchema.extend({
  pbdocNumber: z.string().min(1, "N√∫mero PBDOC √© obrigat√≥rio"),
  description: z.string().min(1, "Objeto √© obrigat√≥rio"),
  centralDeCompras: z.string().optional(),
  modalityId: z.number({
    required_error: "Por favor, selecione a modalidade",
  }).refine(val => val > 0, "Modalidade √© obrigat√≥ria"),
  sourceId: z.number({
    required_error: "Por favor, selecione a fonte de recurso",
  }).refine(val => val > 0, "Fonte de recurso √© obrigat√≥ria"),
  responsibleId: z.number({
    required_error: "Por favor, selecione o respons√°vel",
  }).refine(val => val > 0, "Respons√°vel √© obrigat√≥rio"),
  priority: z.enum(["low", "medium", "high"], {
    required_error: "Por favor, selecione a prioridade",
  }),
});

type ProcessFormValues = z.infer<typeof processFormSchema>;

interface ProcessFormProps {
  defaultValues?: Partial<ProcessFormValues>;
  initialData?: any;
  onSubmit: (data: ProcessFormValues) => void;
  isSubmitting: boolean;
}

const ProcessForm = ({ defaultValues, initialData, onSubmit, isSubmitting }: ProcessFormProps) => {
  // Get users for responsible selector
  const { data: users, isLoading: usersLoading } = useQuery<User[]>({
    queryKey: ['/api/users'],
  });

  // Get modalities for modality selector
  const { data: modalities, isLoading: modalitiesLoading } = useQuery<BiddingModality[]>({
    queryKey: ['/api/modalities'],
  });

  // Get resource sources for source selector
  const { data: sources, isLoading: sourcesLoading } = useQuery<ResourceSource[]>({
    queryKey: ['/api/sources'],
  });

  // Get departments for department selector
  const { data: departments, isLoading: departmentsLoading } = useQuery<Department[]>({
    queryKey: ['/api/departments'],
  });

  // Combine defaultValues and initialData, with initialData taking precedence
  const combinedDefaultValues = {
    pbdocNumber: "",
    description: "",
    modalityId: 0,
    sourceId: 0,
    responsibleId: 0,
    centralDeCompras: "",
    priority: "medium",
    ...defaultValues,
    ...(initialData && {
      pbdocNumber: initialData.pbdocNumber,
      description: initialData.description,
      modalityId: initialData.modalityId,
      sourceId: initialData.sourceId,
      responsibleId: initialData.responsibleId,
      centralDeCompras: initialData.centralDeCompras || "",
      priority: initialData.priority,
      status: initialData.status,
    }),
  };

  const form = useForm<ProcessFormValues>({
    resolver: zodResolver(processFormSchema),
    defaultValues: combinedDefaultValues,
  });

  // Fun√ß√£o para lidar com o envio do formul√°rio, com log do valor
  const handleFormSubmit = (data: ProcessFormValues) => {
    console.log("Enviando dados do formul√°rio:", data);
    onSubmit(data);
  };

  const isLoading = usersLoading || modalitiesLoading || sourcesLoading || departmentsLoading;

  return (
    <Card className="w-full max-w-none">
      <CardHeader className="pb-3">
        <CardTitle className="text-lg md:text-xl">
          {defaultValues ? "Editar Processo" : "Novo Processo"}
        </CardTitle>
        <CardDescription className="text-sm">
          {defaultValues 
            ? "Atualize os detalhes do processo de licita√ß√£o" 
            : "Cadastre um novo processo de licita√ß√£o"}
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-4">
        {isLoading ? (
          <p>Carregando formul√°rio...</p>
        ) : (
          <Form {...form}>
            <form onSubmit={form.handleSubmit(handleFormSubmit)} className="space-y-3 md:space-y-4">
              <div className="grid grid-cols-1 lg:grid-cols-2 gap-3 md:gap-4">
                <FormField
                  control={form.control}
                  name="pbdocNumber"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex">
                        N√∫mero PBDOC <span className="text-red-500 ml-1">*</span>
                      </FormLabel>
                      <FormControl>
                        <Input placeholder="Ex: PB-2023-5482" {...field} />
                      </FormControl>
                      <FormDescription>
                        N√∫mero de protocolo do documento no sistema PBDOC (obrigat√≥rio)
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="modalityId"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel className="flex">
                        Modalidade <span className="text-red-500 ml-1">*</span>
                      </FormLabel>
                      <Select 
                        onValueChange={(value) => field.onChange(parseInt(value))}
                        value={field.value ? field.value.toString() : undefined}
                      >
                        <FormControl>
                          <SelectTrigger>
                            <SelectValue placeholder="Selecione a modalidade" />
                          </SelectTrigger>
                        </FormControl>
                        <SelectContent>
                          {modalities?.map((modality) => (
                            <SelectItem 
                              key={modality.id} 
                              value={modality.id.toString()}
                            >
                              {modality.name}
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                      <FormDescription>
                        Selecione a modalidade de licita√ß√£o (obrigat√≥rio)
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </div>

              <FormField
                control={form.control}
